INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1361005, 'Ribavirin', 'Azathioprine', 'Major', 'Published case reports suggest that ribavirin may potentiate the myelotoxic effects of 6-mercaptopurine and its prodrug, azathioprine. The proposed mechanism is inhibition of inosine monophosphate dehydrogenase by ribavirin, which interferes with one of the metabolic pathways of 6-mercaptopurine and leads to accumulation of the myelotoxic metabolite 6-methylthioinosine monophosphate (6-MTIMP). Theoretically, inhibition of inosine monophosphate dehydrogenase may also reduce the pharmacologic effects of 6-mercaptopurine and azathioprine by inhibiting conversion to the active metabolite; however, this was not observed in the case reports.', 'DDInter', 1767317475, 'The use of ribavirin in combination with 6-mercaptopurine or azathioprine should generally be avoided if possible. Patients receiving the combination should have complete blood counts, including platelet counts, monitored weekly for the first month, twice monthly for the second and third months, then monthly or more frequently if dosage or other therapy changes are necessary. Treatment with these medications should be discontinued promptly if pancytopenia develops, and the combination should not be reintroduced following recovery.', 'Metabolism', 'The use of ribavirin in combination with 6-mercaptopurine or azathioprine should generally be avoided if possible.', NULL, NULL, 'Major', 'pharmacodynamic');
