INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1361031, 'Strontium chloride Sr-89', 'Azathioprine', 'Moderate', 'The concomitant use of bone marrow depressants and strontium-89 chloride may have additive myelosuppressive effects.', 'DDInter', 1767317475, 'Patients should be monitored for excessive bone marrow suppression during treatment with strontium-89 chloride. Dose reductions of the other bone marrow depressants may be necessary. Patients should be advised to contact their physician if they develop signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, easy bruising or bleeding, or signs of infection such as fever, chills, sore throat, body aches, and other influenza-like symptoms.', 'Synergism', 'Patients should be monitored for excessive bone marrow suppression during treatment with strontium-89 chloride.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1361032, 'Succinylcholine', 'Azathioprine', 'Minor', 'Limited data suggest that azathioprine may variously enhance, reduce, or reverse the effects of neuromuscular blockers. The mechanism for enhancement of effects is unknown. The mechanism for reduction of effects may be related to inhibition of phosphodiesterase in motor nerve terminals. Routine dose titration and standard monitoring of the patient are recommended.', 'DDInter', 1767317475, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1361033, 'Sulfasalazine', 'Azathioprine', 'Moderate', 'Aminosalicylate derivatives (e.g., balsalazide, mesalamine, olsalazine, sulfasalazine) may potentiate the pharmacologic effects of purine antagonist antimetabolites (e.g., azathioprine, mercaptopurine, thioguanine).', 'DDInter', 1767317475, 'Caution is advised if aminosalicylate derivatives must be used concomitantly with purine antagonist antimetabolites. Patients receiving the combination should be closely monitored for hematologic toxicity, and the antimetabolite dosage adjusted accordingly.', 'Synergism', 'Caution is advised if aminosalicylate derivatives must be used concomitantly with purine antagonist antimetabolites.', NULL, NULL, 'Moderate', 'pharmacodynamic');
