INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1361052, 'Trandolapril', 'Azathioprine', 'Moderate', 'Limited clinical data suggest that angiotension converting enzyme (ACE) inhibitors and azathioprine may have additive hematologic effects, resulting in an increased risk of neutropenia or leukopenia during coadministration. Data have been reported for captopril and enalapril. It is unknown whether this effect could occur with other ACE inhibitors.', 'DDInter', 1767317475, 'Patients should be monitored for clinical and laboratory evidence of hematologic abnormalities during concomitant administration. Patients should be advised to report symptoms such as unusual bleeding, bruising, fever, sore throat, mouth sores, or symptoms of infection.', 'Synergism', 'Patients should be monitored for clinical and laboratory evidence of hematologic abnormalities during concomitant administration.', NULL, NULL, 'Moderate', 'pharmacodynamic');
