INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1361054, 'Trichophyton mentagrophytes', 'Azathioprine', 'Moderate', 'Immunosuppressed patients may have diminished response to diagnostic skin test antigens due to suppression of cell-mediated, delayed-type hypersensitivity. Falsely insignificant or false-negative results may occur in such patients, which may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317475, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', 'Others', 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1361055, 'Trimethoprim', 'Azathioprine', 'Moderate', 'Even though the combination is used in clinical practice, the risk of bone marrow suppression may be increased when azathioprine or mercaptopurine is coadministered with trimethoprim or sulfamethoxazole-trimethoprim (co-trimoxazole). The risk may be increased when concurrent therapy is longer than 3 weeks. Thrombocytopenia and neutropenia have been reported; however, data have been conflicting. The mechanism is unknown, but may be related to additive myelosuppressive effects and hemodialysis-induced folate depletion.', 'DDInter', 1767317475, 'Close clinical and laboratory monitoring for the development of hematologic adverse effects is recommended both during and after discontinuation of therapy. Dosage adjustment of azathioprine or mercaptopurine may be considered if an interaction is suspected. Patients should be advised to promptly report symptoms such as chills, fever, malaise, sore throat, mouth sores, unusual fatigue, bruising, or bleeding.', 'Synergism', 'Close clinical and laboratory monitoring for the development of hematologic adverse effects is recommended both during and after discontinuation of therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
