INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1361188, 'Garlic', 'Azilsartan medoxomil', 'Minor', 'Garlic has been found in some studies to lower blood pressure and may theoretically potentiate the effects of hypotensive agents. A meta-analysis of 11 randomized, placebo-controlled studies showed a mean decrease of 4.6 mmHg for systolic blood pressure and no significant effect for diastolic blood pressure in subjects treated with garlic (most often garlic powder 600 to 900 mg/day, providing 3.6 to 5.4 mg of the active component allicin) compared to placebo.', 'DDInter', 1767317475, '-', 'Synergism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1361189, 'Guanfacine', 'Azilsartan medoxomil', 'Moderate', 'Use of guanfacine in combination with antihypertensive agents may result in additive pharmacodynamic effects such as hypotension and syncope.', 'DDInter', 1767317475, 'Caution is advised during coadministration of these agents. Dosage adjustments may be required and hemodynamic responses should be monitored during concomitant therapy. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery until they know how the medications affect them.', 'Synergism', 'Caution is advised during coadministration of these agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
