    ) VALUES (1361346, 'Aluminum hydroxide', 'Azithromycin', 'Moderate', 'INTERVAL: The concomitant administration of aluminum or magnesium-containing antacids may reduce peak serum concentrations (Cmax) of oral azithromycin, but does not affect total absorption (AUC).', NULL, 'Azithromycin should be administered at least 1 hour before or 2 hours after antacids.', NULL, 'Azithromycin should be administered at least 1 hour before or 2 hours after antacids.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
