INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (125257, 'Nabumetone', 'Labetalol', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may attenuate the antihypertensive effect of beta-blockers. The proposed mechanism is NSAID-induced inhibition of renal prostaglandin synthesis, which results in unopposed pressor activity producing hypertension. In addition, NSAIDs can cause fluid retention, which also affects blood pressure. Indomethacin and piroxicam have been reported to have greater attenuating effects than other NSAIDs, and indomethacin effects may be significant in patients with eclampsia.', NULL, 'Patients receiving a beta-blocker who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID.', NULL, 'Patients receiving a beta-blocker who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID. The interaction is not expected to occur with low doses (e.g., low-dose aspirin) or intermittent short-term administration of NSAIDs.', 'Antagonism', 'Misoprostol, Chondroitin sulfate, Esomeprazole, Glucosamine', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/125407/', '[1] Radack KL, Deck CC, Bloomfield SS "Ibuprofen interferes with the efficacy of antihypertensive drugs." Ann Intern Med 107 (1987): 628-35[2] Hartmann D, Stief G, Lingenfelder M, Guzelhan C, Horsch AK "Study on the possible interaction between tenoxicam and atenolol in hypertensive patients." Arzneimittelforschung 45-1 (1995): 494-8[3] Abate MA, Neely JL, Layne RD, D''Allessandri R "Interaction of indomethacin and sulindac with labetalol." Br J Clin Pharmacol 31 (1991): 363-6[4] Salvetti A, Pedrinelli R, Alberici P, Magagna A, Abdel-Haq B "The influence of indomethacin and sulindac on some pharmacological actions of atenolol in hypertensive patients." Br J Clin Pharmacol 17 Suppl 1 (1984): s108-11[5] Wong DG, Spence JD, Lamki L, Freeman D, McDonald JW "Effect of non-steroidal anti-inflammatory drugs on control of hypertension by beta-blockers and diuretics." Lancet 1 (1986): 997-1001[6] Ylitalo P, Pitkajarvi T, Pyykonen ML, Nurmi AK, Seppala E, Vapaatalo H "Inhibition of prostaglandin synthesis by indomethacin interacts with the antihypertensive effect of atenolol." Clin Pharmacol Ther 38 (1985): 443-9[7] Durao V, Prata MM, Concalves LM "Modification of antihypertensive effect of B-adrenoceptor-blocking agents by inhibition of endogenous prostaglandin synthesis." Lancet 2 (1977): 1005-7[8] Salvetti A, Arzilli F, Pedrinelli R, Beggi P, Motolese M "Interaction between oxprenolol and indomethacin on blood pressure in essential hypertensive patients." Eur J Clin Pharmacol 22 (1982): 197-201[9] Durao V, Prata MM, Goncalves LM "Modification of antihypertensive effect of beta-adrenoceptor-blocking agents by inhibition of endogenous prostaglandin synthesis." Lancet 2 (1977): 1005-7[10] Salvetti A, Pedrinelli R, Alberici P, Magagna A, Abdel-Haq B "The influence of indomethacin and sulindac on some pharmacological actions of atenolol in hypertensive patients." Br J Clin Pharmacol 17 Suppl 1 (1984): s108-11', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (125258, 'Nabumetone', 'Leflunomide', 'Major', 'The recent, concomitant, or subsequent use (without the recommended leflunomide washout period or procedure) of other agents known to induce hepatotoxicity including NSAIDs and salicylates may potentiate the risk of liver injury associated with leflunomide. The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide. Pharmacokinetically, the active metabolite of leflunomide has been shown to cause increases of 13% to 50% in the free fraction of diclofenac and ibuprofen. Additionally, in vitro studies indicate that the metabolite inhibits hepatic microsomal enzyme CYP450 2C9, which participates in the metabolism of many NSAIDs.', NULL, 'Patients receiving leflunomide or teriflunomide in combination with NSAIDs should be closely monitored for hepatotoxicity.', NULL, 'Patients receiving leflunomide or teriflunomide in combination with NSAIDs should be closely monitored for hepatotoxicity. Liver enzymes and bilirubin should be measured prior to initiation of leflunomide/teriflunomide therapy and at least monthly for the first six months of treatment and every 6 to 8 weeks thereafter. Patients with preexisting liver disease or elevated baseline liver enzymes (i.e., ALT greater than two times the upper limit of normal) should not receive leflunomide or teriflunomide. Patients who develop elevated serum ALT greater than three times ULN while receiving these medications should discontinue treatment and be given washout procedures with cholestyramine or activated charcoal to accelerate elimination of leflunomide''s active metabolite from plasma, which otherwise may take up to two years.', 'Synergism', 'Chondroitin sulfate, Esomeprazole, Glucosamine', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/125408/', '[1] "Product Information. Arava (leflunomide)." Hoechst Marion-Roussel Inc, Kansas City, MO.[2] "Product Information. Aubagio (teriflunomide)." Genzyme Corporation, Cambridge, MA.[3] EMEA "EMEA public statement on leflunomide (ARAVA) - severe and serious hepatic reactions. Available from URL: http://www.eudra.org/emea.html." ([1999 Sept 2]):', 1767369485);
