INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (125305, 'Piroxicam', 'Nabumetone', 'Moderate', 'Concomitant use of more than one nonsteroidal anti-inflammatory drug (NSAID) at a time may increase the potential for serious gastrointestinal toxicity including inflammation, bleeding, ulceration, and perforation of the esophagus, stomach, or intestines. These events can occur at any time during NSAID use, with or without warning symptoms. The risk is dependent on both dosage and duration of therapy.', NULL, 'Concomitant use of more than one NSAID at a time should generally be avoided.', NULL, 'Concomitant use of more than one NSAID at a time should generally be avoided. Patients treated with an NSAID should be advised to take it with food and to immediately report signs and symptoms of GI ulceration and bleeding such as severe abdominal pain, dizziness, lightheadedness, and the appearance of black, tarry stools. The selective use of prophylactic anti-ulcer therapy (e.g., antacids, misoprostol, proton pump inhibitors) may be considered in high risk patients.', 'Synergism', 'Piroxicam, Chondroitin sulfate, Esomeprazole, Glucosamine', 'Chondroitin sulfate, Esomeprazole, Glucosamine, Nabumetone, Tolazoline, Dimethyl sulfoxide, Fluocinolone acetonide, Salicylic acid, Fluorometholone', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/125455/', '[1] "Product Information. Duexis (famotidine-ibuprofen)." Horizon Pharma USA Inc, Northbrook, IL.[2] "Product Information. Mobic (meloxicam)" Boehringer-Ingelheim, Ridgefield, CT.[3] "Product Information. Ponstel (mefenamic acid)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[4] "Product Information. Naprosyn (naproxen)." Syntex Laboratories Inc, Palo Alto, CA.[5] "Product Information. VIMOVO (esomeprazole-naproxen)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[6] "Product Information. Caldolor (ibuprofen)." Cumberland Pharmaceuticals Inc, Nashville, TN.[7] "Product Information. Meclofenamate Sodium (meclofenamate)." Mylan Pharmaceuticals Inc, Morgantown, WV.[8] "Product Information. Lodine (etodolac)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[9] "Product Information. Daypro (oxaprozin)." Searle, Skokie, IL.[10] "Product Information. Anaprox (naproxen)." Roche Laboratories, Nutley, NJ.[11] "Product Information. Nalfon (fenoprofen)." Xspire Pharma, Ridgeland, MS.[12] "Product Information. Etodolac ER (etodolac)." Taro Pharmaceuticals U.S.A. Inc, Hawthorne, NY.[13] "Product Information. Cambia (diclofenac)." Kowa Pharmaceuticals America (formerly ProEthic), Montgomery, AL.[14] "Product Information. Ketoprofen ER (ketoprofen)." Mylan Pharmaceuticals Inc, Morgantown, WV.[15] "Product Information. Indocin (indomethacin)." Merck & Co, Inc, West Point, PA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (125306, 'Pitolisant', 'Nabumetone', 'Moderate', 'Concomitant use of pitolisant with NSAIDs and/or corticosteroids may increase the risk of gastrointestinal adverse effects such as dyspepsia, abdominal pain or discomfort, and gastritis due to potential additive irritant effects on the gastrointestinal mucosa. However, the effects were described as mostly mild to moderate in severity. Data are not available on the potential for serious gastrointestinal (GI) toxicity, including inflammation, bleeding, ulceration, and perforation.', NULL, 'Caution is advised if pitolisant is used in combination with NSAIDs and/or corticosteroids, particularly in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly or debilitated patients.', NULL, 'Caution is advised if pitolisant is used in combination with NSAIDs and/or corticosteroids, particularly in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly or debilitated patients. If concomitant therapy is required, patients should be advised to report signs and symptoms of adverse GI effects, including abdominal pain or discomfort, dyspepsia, gastroesophageal reflux disease, gastritis, or the appearance of black, tarry stools.', 'Synergism', 'Rabeprazole, Misoprostol, Glucosamine, Chondroitin sulfate, Rofecoxib, Esomeprazole, Valdecoxib', 'Dextromethorphan, Valbenazine, Dalfampridine, Sodium oxybate, Riluzole, Tetrabenazine, Amifampridine, Deutetrabenazine, Tafamidis', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/125456/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (125307, 'Plazomicin', 'Nabumetone', 'Moderate', 'The nephrotoxic effect of aminoglycosides may be potentiated by nonsteroidal anti-inflammatory drugs (NSAIDs), particularly if the latter had been given in high dosages for prolonged periods. Animal models suggest that renal prostaglandins may play a role in maintaining normal renal blood flow and glomerular filtration rate during the development of aminoglycoside nephrotoxicity, thus inhibition of their production by NSAIDs may worsen the renal damage. In premature infants, NSAIDs may increase the plasma concentrations of aminoglycosides. The proposed mechanism is decreased aminoglycoside clearance due to NSAID reduction of glomerular filtration rate.', NULL, 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy.', NULL, 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy. If concomitant administration is necessary, hydration status as well as renal and vestibular functions should be closely monitored.', 'Synergism', 'Misoprostol, Chondroitin sulfate, Glucosamine', 'Neomycin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/125457/', '[1] Gagliardi L "Possible indomethacin-aminoglycoside interaction in preterm infants." J Pediatr 107 (1985): 991-2[2] Farag MM, Mikhail MR, Abdel-Meguid E, Abdel-Tawab S "Assessment of gentamicin-induced nephrotoxicity in rats treated with low doses of ibuprofen and diclofenac sodium." Clin Sci 91 (1996): 187-91[3] Scott CS, RetschBogart GZ, Henry MM "Renal failure and vestibular toxicity in an adolescent with cystic fibrosis receiving gentamicin and standard-dose ibuprofen." Pediat Pulm 31 (2001): 314-6[4] Kovesi TA, Swartz R, MacDonald N "Transient renal failure due to simultaneous ibuprofen and aminoglycoside therapy in children with cystic fibrosis." N Engl J Med 338 (1998): 65-6[5] Assael BM, Chiabrando C, Gagliardi L, Noseda A, Bamonte F, Salmona M "Prostaglandins and aminoglycoside nephrotoxicity." Toxicol Appl Pharmacol 78 (1985): 386-94[6] Zarfin Y, Koren G, Maresky D, et al "Clinical and laboratory observations: possible indomethacin-aminoglycoside interaction in preterm infants." J Pediatr 106 (1985): 511-3', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (125308, 'Polythiazide', 'Nabumetone', 'Moderate', 'Concomitant use of nonsteroidal anti-inflammatory drugs (NSAIDs) and diuretics may adversely affect renal function due to NSAID inhibition of the renal synthesis of prostaglandins that help maintain renal perfusion in dehydrated states. The risk may be increased in patients on dietary sodium restriction. At the same time, hypotensive effect of the diuretics may be reduced because inhibition of prostaglandins can lead to unopposed pressor activity and, consequently, elevation in blood pressure. Natriuretic and diuretic effects may also be reduced, as NSAIDs have been reported to cause sodium and water retention, which may account for the increased risk of congestive heart failure associated with the combination. NSAIDs may also increase the risk of hyperkalemia associated with potassium-sparing diuretics.', NULL, 'In patients receiving both diuretic and NSAID therapy, management consists of avoiding dehydration and carefully monitoring the patient''s renal function and blood pressure.', NULL, 'In patients receiving both diuretic and NSAID therapy, management consists of avoiding dehydration and carefully monitoring the patient''s renal function and blood pressure. If renal insufficiency or hyperkalemia develops, both drugs should be discontinued until the condition is corrected.', 'Antagonism', 'Misoprostol, Chondroitin sulfate, Glucosamine', 'Tinidazole, Ascorbic acid, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Amphotericin B, Ciclopirox, Vemurafenib, Darunavir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/125458/', '[1] Muller F