INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (125373, 'Trovafloxacin', 'Nabumetone', 'Moderate', 'Coadministration with nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of central nervous system toxicity sometimes associated with fluoroquinolone use. Some investigators suggest that the piperazine ring of fluoroquinolones may inhibit the binding of gamma-aminobutyric acid (GABA) to brain receptors and that NSAIDs may synergistically add to this effect. Patients with a history of seizures may be at greater risk.', NULL, 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', NULL, 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', 'Synergism', 'Rabeprazole, Misoprostol, Chondroitin sulfate, Glucosamine, Esomeprazole', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/125523/', '[1] "Product Information. Factive (gemifloxacin)." GeneSoft Inc, San Francisco, CA.[2] "Product Information. Avelox (moxifloxacin)" Bayer, West Haven, CT.[3] Davey PG "Overview of drug interactions with the quinolones." J Antimicrob Chemother 22(suppl c) (1988): 97-107[4] Domagala JM "Structure-activity and structure-side-effect relationships for the quinolone antibacterials." J Antimicrob Chemother 33 (1994): 685-706[5] "Product Information. Cipro (ciprofloxacin)." Bayer, West Haven, CT.[6] "Product Information. Floxin (ofloxacin)." Ortho Pharmaceutical Corporation, Raritan, NJ.[7] "Product Information. Tequin (gatifloxacin)" Bristol-Myers Squibb, Princeton, NJ.[8] Segev S. Rehavi M, Rubinstein E "Quinolones, theophylline, and diclofenac interactions with the gamma-aminobutyric acid receptor." Antimicrob Agents Chemother 32 (1988): 1624-6[9] Hooper DC, Wolfson JS "The fluoroquinolones: pharmacology, clinical uses, and toxicities in humans." Antimicrob Agents Chemother 28 (1985): 716-21[10] Ball P "Ciprofloxacin: an overview of adverse experiences." J Antimicrob Chemother 18 (1986): 187-93[11] "Product Information. Penetrex (enoxacin)." Rhone-Poulenc Rorer, Collegeville, PA.[12] "Product Information. Raxar (grepafloxacin)." Glaxo Wellcome, Research Triangle Park, NC.[13] Ball P, Tillotson G "Tolerability of fluoroquinolone antibiotics: past, present and future." Drug Saf 13 (1996): 343-8[14] "Product Information. Levaquin (levofloxacin)." Ortho Pharmaceutical Corporation, Raritan, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (125374, 'Urokinase', 'Nabumetone', 'Moderate', 'Systemically and topically administered nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of bleeding in patients treated with anticoagulants and other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The pharmacologic effects of NSAIDs that contribute to this interaction include prolongation of prothrombin time and inhibition of platelet adhesion and aggregation.', NULL, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis.', NULL, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Protein C, Acenocoumarol, Selexipag', 'Rabeprazole, Misoprostol, Chondroitin sulfate, Glucosamine, Esomeprazole', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/125524/', '[1] "Product Information. Nevanac (nepafenac ophthalmic)." Alcon Laboratories Inc, Fort Worth, TX.[2] "Product Information. Acular (ketorolac)." Allergan Inc, Irvine, CA.[3] "Product Information. Xibrom (bromfenac ophthalmic)." ISTA Pharmaceuticals, Irvine, CA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (125375, 'Valdecoxib', 'Nabumetone', 'Moderate', 'Concomitant use of COX-2 inhibitors with other nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the potential for serious gastrointestinal toxicity including inflammation, bleeding, ulceration, and perforation. The risk is dependent on both dosage and duration of therapy.', NULL, 'The use of COX-2 inhibitors with non-aspirin NSAIDs should be avoided.', NULL, 'The use of COX-2 inhibitors with non-aspirin NSAIDs should be avoided. Some authorities consider the use of COX-2 inhibitors with non-aspirin NSAIDs as contraindicated.', 'Synergism', 'Valdecoxib, Chondroitin sulfate, Esomeprazole, Glucosamine', 'Chondroitin sulfate, Esomeprazole, Glucosamine, Nabumetone', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/125525/', '[1] Cerner Multum, Inc "ANVISA Bulrio Eletrnico. Available from: URL: http://www.anvisa.gov.br/datavisa/fila_bula/index.asp." O 0 (2015):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Celebrex (celecoxib)." Searle, Chicago, IL.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (125376, 'Vasopressin', 'Nabumetone', 'Moderate', 'The antidiuretic response to vasopressin or desmopressin may be potentiated by certain drugs, including nonsteroidal anti-inflammatory agents, selective serotonin reuptake inhibitors, tricyclic/tetracyclic antidepressants, carbamazepine, chlorpropamide, chlorpromazine, clofibrate, eslicarbazepine, fludrocortisone, haloperidol, lamotrigine, oxcarbazepine, urea, and some antineoplastic agents (e.g., vinca alkaloids, cisplatin, cyclophosphamide). These drugs can occasionally cause fluid retention, in some cases secondary to the syndrome of inappropriate antidiuretic hormone secretion (SIADH). Coadministration with vasopressin or desmopressin may, therefore, increase the risk of water intoxication and/or hyponatremia. Seizure and coma in association with severe hyponatremia have been reported during concomitant use of desmopressin and imipramine or ibuprofen.', NULL, 'Caution is advised when vasopressin or desmopressin is used in combination with drugs that can cause fluid retention or SIADH.', NULL, 'Caution is advised when vasopressin or desmopressin is used in combination with drugs that can cause fluid retention or SIADH. Some authorities recommend adjusting vasopressin dosage as needed. Serum electrolytes, especially sodium, as well as BUN and creatinine should be monitored regularly. Patients should be advised to seek medical attention if they develop signs and symptoms of water intoxication or hyponatremia such as anorexia, nausea, vomiting, headache, malaise, lethargy, irritability, difficulty concentrating, memory impairment, confusion, weakness, muscle spasm, unsteadiness (which may lead to falls), anuria, and weight gain. Severe hyponatremia can be life-threatening if it is not promptly diagnosed and treated; therefore, early treatment is important to help prevent progression to seizures, coma, respiratory arrest, and death.', 'Synergism', 'Rabeprazole, Misoprostol, Chondroitin sulfate, Glucosamine, Esomeprazole', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/125526/', '[1] Vanhees SL, Paridaens R, Vansteenkiste JF "Syndrome of inappropriate antidiuretic hormone associated with chemotherapy-induced tumour lysis in small-cell lung cancer: case report and literature review." Ann Oncol 11 (2000): 1061-5[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Kosmidis HV, Bouhoutsou DO, Varvoutsi MC, et al. "Vincristine overdose: experience with 3 patients." Pediatr Hematol Oncol 8 (1991): 171-8[4] Maramattom BV "Duloxetine-induced syndrome of inappropriate antidiuretic hormone secretion and seizures." Neurology 66 (2006): 773-4[5] ten Holt WL, van Iperen CE, Schrijver G, Bartelink AK "Severe hyponatremia during therapy with fluoxetine." Arch Intern Med 156 (1996): 681-2[6] Miller MG "Tricyclics as a possible cause of hyponatremia in psychiatric patients." Am J Psychiatry 146 (1989): 807[7] Petersson I, Nilsson G, Hansson B-G, Hedner T "Water intoxication associated with non-steroidal anti-inflammatory drug therapy." Acta Med Scand 221 (1987): 221-3[8] Nielsen OA, Johannessen AC, Bardrum B "Oxcarbazepine-induced hyponatremia, a cross-sectional study." Epilepsy Res 2 (1988): 269-71[9] Schattner A, Skurnik Y "Fluoxetine-induced SIADH." J Am Geriatr Soc 44 (1996): 1413[10] Tanay A, Firemann Z, Yust I, Abramov AL "Chlorpropamide-induced syndrome of inappropriate antidiuretic hormone secretion." J Am Geriatr Soc 29 (1981): 334-6[11] Catalano G, Kanfer SN, Catalano MC, Alberts VA "The role of sertraline in a patient with recurrent hyponatremia." Gen Hosp Psychiatry 18 (1996): 278-83[12] Kokko H, Hall PD, Afrin