INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (62745, 'Ambenonium', 'Clidinium', 'Moderate', 'Anticholinergic agents and other agents with significant anticholinergic activity (e.g., clozapine, class IA antiarrhythmics especially disopyramide) may antagonize the effects of cholinergic skeletal muscle stimulants (e.g., ambenonium, edrophonium, guanidine, neostigmine, pyridostigmine). Although this interaction may be desirable in some situations, such as when atropine is used to treat excessive muscarinic side effects and cholinergic crisis induced by anticholinesterase overdose, unintentional or indiscriminate use of anticholinergic agents in the treatment of myasthenia gravis may exacerbate symptoms.', NULL, 'Agents with potent anticholinergic activity should preferably be avoided in patients receiving cholinergic skeletal muscle stimulants.', NULL, 'Agents with potent anticholinergic activity should preferably be avoided in patients receiving cholinergic skeletal muscle stimulants. If concurrent use is necessary, patients treated for myasthenia gravis should be monitored for potential exacerbation of symptoms.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62831/', '[1] "Product Information. Mestinon (pyridostigmine)." ICN Pharmaceuticals Inc, Cost Mesa, CA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (62746, 'Clidinium', 'Arbutamine', 'Moderate', 'Atropine and other agents with significant anticholinergic activity (e.g., clozapine) may enhance the chronotropic response to arbutamine due to their blockade of normal vagal inhibition of the sinoatrial node, resulting in increased sinoatrial node automaticity.', NULL, 'As the dosing of arbutamine is based on the heart rate response of the patient, the concomitant use of atropine or other anticholinergic agents should be avoided.', NULL, 'As the dosing of arbutamine is based on the heart rate response of the patient, the concomitant use of atropine or other anticholinergic agents should be avoided.', 'Synergism', 'Dobutamine, Norepinephrine, Midodrine, Amrinone, Droxidopa, Angiotensin II, Ephedrine, Milrinone, Fenoldopam, Isoprenaline, Metaraminol, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62832/', '[1] "Product Information. GenESA (arbutamine)." Gensia Inc, San Diego, CA.', 1767369485);
