INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (63173, 'Chloroquine', 'Tolterodine', 'Minor', 'Coadministration with drugs that are inhibitors of CYP450 2D6 may increase the plasma concentrations of tolterodine, which is primarily metabolized by this isoenzyme in patients that are extensive metabolizers, or EMs (approximately 93% of Caucasians and more than 98% of Asians and individuals of African descent). Moreover, since the active 5-hydroxymethyl metabolite (5-HM) generated by CYP450 2D6 is equipotent compared with tolterodine, and the sum of unbound serum concentrations of tolterodine and 5-HM are only 25% higher during the interaction, little alteration in the overall pharmacologic activity would be expected. Thus, dosage reductions should not be necessary.', NULL, '-', NULL, '-', 'Metabolism', 'Artesunate, Mefloquine, Artemether, Pyrimethamine, Tafenoquine, Atovaquone, Proguanil, Sulfadoxine', 'Pentosan polysulfate, Calcium chloride, Phenazopyridine, Sildenafil, Trospium, Mirabegron, Alprostadil, Acetohydroxamic acid, Yohimbine, Flavoxate, Avanafil, More', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/63259/', '[1] Brynne N, Svanstrom C, AbergWistedt A, Hallen B, Bertilsson L "Fluoxetine inhibits the metabolism of tolterodin-pharmacokinetic implications and proposed clinical relevance." Br J Clin Pharmacol 48 (1999): 553-63[2] "Product Information. Detrol (tolterodine)." Pharmacia and Upjohn, Kalamazoo, MI.', 1767369485);
