INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (63246, 'Demeclocycline', 'Chlorothiazide', 'Minor', 'The coadministration of diuretics and tetracyclines may result in decreased renal function manifested by increases in serum creatinine and blood urea nitrogen (BUN). Usually, no clinical intervention is necessary, unless decreases in renal function occur. If renal function deteriorates, discontinuation of one or both agents may be necessary.', NULL, '-', NULL, '-', 'Others', 'Tigecycline, Sarecycline, Omadacycline, Eravacycline, Mupirocin, Rifamycin, Bacitracin, Chloramphenicol, Rifaximin, Gentamicin, Amikacin, Neomycin', 'Tamsulosin, Tinidazole, Ascorbic acid, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Mitapivat, Amphotericin B, Clindamycin, Ciclopirox, More', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/63332/', '[1] Jick H, Slone D, Shapiro S, et al "Tetracycline and drug-attributed rises in blood urea nitrogen: a report from the Boston Collaborative Drug Surveillance Program." JAMA 220 (1972): 377-9[2] Alexander MR "Tetracycline and rises in urea nitrogen." JAMA 221 (1972): 713-4[3] Garty M, Hurwitz A "Effect of cimetidine and antacids on gastrointestinal absorption of tetracycline." Clin Pharmacol Ther 28 (1980): 203-7[4] Tannenberg AM "Tetracycline and rises in urea nitrogen." JAMA 221 (1972): 713[5] "Product Information. Achromycin (tetracycline)." Lederle Laboratories, Wayne, NJ.[6] "Product Information. Declomycin (demeclocycline)." Lederle Laboratories, Wayne, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (63247, 'Deserpidine', 'Chlorothiazide', 'Moderate', 'The hypotensive effects of thiazide diuretics and alpha-adrenergic blockers may be additive. Postural hypotension may occur.', NULL, 'Hemodynamic responses should be monitored during coadministration, especially during the first few weeks of therapy.', NULL, 'Hemodynamic responses should be monitored during coadministration, especially during the first few weeks of therapy. Patients should be advised to take the alpha-blocker at bedtime and to notify their physician if they experience dizziness or syncope while awake.', 'Synergism', 'Trichlormethiazide, Tamsulosin, Tinidazole, Ascorbic acid, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, More', 'Clonidine, Clonidine, Methyldopa', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/63333/', '[1] "Product Information. Xatral (alfuzosin)." Sanofi-Synthelabo Canada Inc, Markham, ON.[2] Cohen J "Long-term efficacy and safety of terazosin alone and in combination with other antihypertensive agents." Am Heart J 122 (1991): 919-25[3] Pool JL "Combination antihypertensive therapy with terazosin and other antihypertensive agents: results of clinical trials." Am Heart J 122 (1991): 926-31[4] Achari R, Laddu A "Terazosin: a new alpha adrenoceptor blocking drug." J Clin Pharmacol 32 (1992): 520-3[5] Kuokkanen K, Mattila MJ "Demonstration of an additive antihypertensive effect of prazosin and polythiazide in out-patient." Curr Ther Res Clin Exp 17 (1975): 431-6', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (63248, 'Desvenlafaxine', 'Chlorothiazide', 'Moderate', 'Coadministration with diuretics may potentiate the risk of hyponatremia associated with the use of selective serotonin reuptake inhibitors (SSRIs) and serotonin-norepinephrine reuptake inhibitors (SNRIs). Elderly patients and patients taking diuretics or who are otherwise volume-depleted may be at greater risk of developing hyponatremia with SSRIs and SNRIs. Antihypertensive agents such as diuretics may potentiate the orthostatic effect that is occasionally observed upon the initiation of SSRI or SNRI therapy. Syncope and orthostatic hypotension tend to occur within the first week of SNRI/SSRI therapy but can occur at any time during treatment, particularly after a dosage increase. The use of SSRIs or SNRIs may also cause sustained increases in blood pressure and heart rate, which may antagonize the therapeutic effects of antihypertensive medications.', NULL, 'Caution is recommended if SSRIs or SNRIs are prescribed in combination with diuretics, particularly in the elderly.', NULL, 'Caution is recommended if SSRIs or SNRIs are prescribed in combination with diuretics, particularly in the elderly. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hyponatremia such as nausea, vomiting, headache, malaise, lethargy, irritability, difficulty concentrating, memory impairment, confusion, weakness, muscle spasm, and unsteadiness (which may lead to falls). More severe and/or acute cases may include hallucination, syncope, seizure, coma, respiratory arrest, and death. Discontinuation of SSRI/SNRI therapy should be considered in patients who develop symptomatic hyponatremia, and appropriate medical intervention instituted as necessary. Patients should also have their blood pressure and pulse monitored before and during SSRI/SNRI therapy, especially during the first few weeks and following a dosage increase. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Tamsulosin, Tinidazole, Ascorbic acid, Metronidazole, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Amphotericin B, Clindamycin, Ciclopirox, More', 'Desipramine, St. John''s Wort, Oxitriptan, Viloxazine, Nortriptyline, Tryptophan, Protriptyline, Esketamine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/63334/', '[1] "Product Information. Savella (milnacipran)." Forest Pharmaceuticals, St. Louis, MO.[2] Jackson C, Carson W, Markowitz J, Mintzer J "SIADH associated with fluoxetine and sertraline therapy." Am J Psychiatry 152 (1995): 809-10[3] Robinson D, Brooks J, Mahler E, Sheikh JI "SIADH--compulsive drinking or SSRI influence?" Ann Pharmacother 30 (1996): 885[4] Odeh M, Beny A, Oliven A "Severe symptomatic hyponatremia during citalopram therapy." Am J Med Sci 321 (2001): 159-60[5] Vishwanath BM, Navalgund AA, Cusano W, Navalgund KA "Fluoxetine as a cause of SIADH." Am J Psychiatry 148 (1991): 542-3[6] Ellison JM, Milofsky JE, Ely E "Fluoxetine-induced bradycardia and syncope in two patients." J Clin Psychiatry 51 (1990): 385-6[7] Jacob S, Spinler SA "Hyponatremia associated with selective serotonin-reuptake inhibitors in older adults." Ann Pharmacother 40 (2006): 1618-22[8] Fitzgerald MA "Hyponatremia associated with SSRI use in a 65-year-old woman." Nurse Pract 33 (2008): 11-2[9] van Campen JP, Voets AJ "SIADH caused by paroxetine." Ann Pharmacother 30 (1996): 1499[10] "Product Information. Effexor (venlafaxine)." Wyeth-Ayerst Laboratories, Philadelph