        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1361596, 'Theophylline', 'Azithromycin', 'Moderate', 'Coadministration with certain macrolide antibiotics may increase the serum concentrations of theophylline, which may result in toxicity. The proposed mechanism is macrolide inhibition of CYP450 3A4, the isoenzyme partially responsible for the metabolic clearance of theophylline.', NULL, 'Pharmacologic response and serum theophylline levels should be monitored more closely whenever a macrolide antibiotic is added to or withdrawn from therapy, and the methylxanthine dosage adjusted as necessary.', NULL, 'Pharmacologic response and serum theophylline levels should be monitored more closely whenever a macrolide antibiotic is added to or withdrawn from therapy, and the methylxanthine dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
