    ) VALUES (1361668, 'Cocaine (topical)', 'Methylene blue', 'Major', 'Cocaine exerts an indirect sympathomimetic effect and may precipitate hypertensive reactions and hyperpyrexia in patients treated with monoamine oxidase inhibitors (MAOIs), as has been reported with other indirect- or mixed-acting sympathomimetic agents such as amphetamine and ephedrine. The mechanism involves a synergistic effect due to enhanced norepinephrine storage in adrenergic neurons (MAOI activity) and decreased reuptake of catecholamines (indirect sympathomimetic activity).', NULL, 'In general, indirect-acting sympathomimetic agents such as cocaine should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.', NULL, 'In general, indirect-acting sympathomimetic agents such as cocaine should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, methylene blue, procarbazine). At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with sympathomimetic agents.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
