        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1362645, 'Aminoglutethimide', 'Dexamethasone', 'Moderate', 'Aminoglutethimide may significantly increase dexamethasone clearance by induction of CYP450 3A4 hepatic microsomal enzymes. It is unknown whether other corticosteroids would be affected to the same extent.', NULL, 'Dexamethasone should not be used in patients receiving aminoglutethimide for adrenal suppression.', NULL, 'Dexamethasone should not be used in patients receiving aminoglutethimide for adrenal suppression. If glucocorticoid replacement is necessary, hydrocortisone is recommended as an alternative because it does not appear to interact.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
