    ) VALUES (1362671, 'Dexamethasone', 'Buprenorphine', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of buprenorphine, which is primarily metabolized by the isoenzyme. Reduced efficacy or withdrawal symptoms may occur in patients maintained on buprenorphine.', NULL, 'Pharmacologic response to buprenorphine should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the buprenorphine dosage adjusted as necessary.', NULL, 'Pharmacologic response to buprenorphine should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the buprenorphine dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
