INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (64673, 'Digoxin', 'Cholestyramine', 'Moderate', 'INTERVAL: Cholestyramine may decrease the absorption and possibly the enterohepatic recirculation of digoxin. Digoxin serum concentrations and pharmacologic effect may be reduced.', NULL, 'Cholestyramine should be administered at least eight hours before or after a dose of digoxin.', NULL, 'Cholestyramine should be administered at least eight hours before or after a dose of digoxin. It may be prudent to monitor patients for altered clinical response and to monitor digoxin levels. Patients should be advised to notify their physician if they experience worsening of their heart symptoms. Conversely, patients should be monitored for digoxin toxicity after discontinuation of cholestyramine.', 'Absorption', 'Digitoxin', 'Fenofibric acid, Mipomersen, Pitavastatin, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Clofibrate, Bempedoic acid, Colesevelam, Alirocumab', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/64759/', '[1] Brown DD, Juhl RP, Warner SL "Decreased bioavailability of digoxin due to hypocholesterolemic interventions." Circulation 58 (1978): 164-72[2] Marcus FI "Pharmacokinetic interactions between digoxin and other drugs." J Am Coll Cardiol 5 (1985): a82-90[3] Rawashdeh NM, al-Hadidi HF, Irshaid YM, Battah AK "Gastrointestinal dialysis of digoxin using cholestyramine." Pharmacol Toxicol 72 (1993): 245-8[4] "Product Information. Questran (cholestyramine)." Par Pharmaceutical Inc, Chestnut Ridge, NY.[5] Hall WH, Shappell SD, Doherty JE "Effect of cholestyramine on digoxin absorption and excretion in man." Am J Cardiol 39 (1977): 213-6[6] Rodin SM, Johnson