INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (64702, 'Cholestyramine', 'Lovastatin', 'Moderate', 'INTERVAL: When administered concomitantly, cholestyramine has been reported to decrease the rate and extent of fluvastatin bioavailability. Other bile acid sequestrants may also decrease the bioavailability of other HMG-CoA reductase inhibitors. The mechanism is most likely due to adsorption of the HMG-CoA reductase inhibitor to the bile acid sequestrant. Separating administration times has been shown to decrease the interaction.', NULL, 'If therapy with both agents is necessary, the HMG-CoA reductase inhibitor should be administered at least 4 hours after the bile acid sequestrant.', NULL, 'If therapy with both agents is necessary, the HMG-CoA reductase inhibitor should be administered at least 4 hours after the bile acid sequestrant.', 'Absorption', 'Mipomersen, Pitavastatin, Evinacumab, Evolocumab, Dextrothyroxine, Probucol, Cholestyramine, Rosuvastatin, Clofibrate, Colestipol, Niacin, More', 'Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Lovastatin, Colestipol, Colesevelam, Alirocumab', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/64788/', '[1] Smith HT, Jokubaitis LA, Troendle AJ, Hwang DS, Robinson WT "Pharmacokinetics of fluvastatin and specific drug interactions." Am J Hypertens 6 (1993): s375-82[2] Richter WO, Jacob BG, Schwandt P "Interaction between fibre and lovastatin." Lancet 338 (1991): 706[3] Lilja JJ, Kivisto KT, Neuvonen PJ "Grapefruit juice-simvastatin interaction: Effect on serum concentrations of simvastatin, simvastatin acid, and HMG-CoA reductase inhibitors." Clin Pharmacol Ther 64 (1998): 477-83[4] Kantola T, Kivisto KT, Neuvonen