INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (64725, 'Cholestyramine', 'Quinestrol', 'Moderate', 'Coadministration with bile-acid binding resins such as cholestyramine may decrease the plasma concentrations and therapeutic effects of estrogens and progestins. The proposed mechanism involves delaying or reducing the absorption of concomitant orally administered drugs by cholestyramine. In addition, it has also been suggested that bile-acid binding resins may interfere with the enterohepatic recycling of estrogens and therefore increase their elimination.', NULL, 'Estrogens and/or progestin-containing oral medications should be administered at least one hour before or four to six hours after bile-acid binding resins such as cholestyramine, colestipol, and colesevelam.', NULL, 'Estrogens and/or progestin-containing oral medications should be administered at least one hour before or four to six hours after bile-acid binding resins such as cholestyramine, colestipol, and colesevelam. Patients receiving this combination should be advised to closely follow dosing schedules to prevent loss of therapeutic effects.', 'Absorption', 'Rosuvastatin, Fenofibrate, Mipomersen, Evinacumab, Dextrothyroxine, Simvastatin, Probucol, Cerivastatin, Lomitapide, Ezetimibe, Niacin, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/64811/', '[1] Underhill KL, Rotter BA, Thompson BK, Prelusky DB, Trenholm HL "Effectiveness of cholestyramine in the detoxification of zearalenone as determined in mice." Bull Environ Contam Toxicol 54 (1995): 128-34[2] "Product Information. Questran (cholestyramine)." Par Pharmaceutical Inc, Chestnut Ridge, NY.[3] Cerner Multum, Inc. "Canadian P