INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (64811, 'Imipramine', 'Choline salicylate', 'Moderate', 'Preliminary evidence suggests that aspirin may increase the plasma free concentration and pharmacologic effects of imipramine by displacing it from plasma protein binding sites.Other salicylates are expected to have similar effects due to their high affinity for plasma proteins.', NULL, 'Pharmacologic response to imipramine should be monitored more closely whenever a salicylate is added to or withdrawn from therapy, and the imipramine dosage adjusted as necessary.', NULL, 'Pharmacologic response to imipramine should be monitored more closely whenever a salicylate is added to or withdrawn from therapy, and the imipramine dosage adjusted as necessary. Patients should be advised to notify their physician if they experience increased adverse effects of imipramine such as dry mouth, constipation, urinary retention, blurred vision, palpitations, and tachycardia.', 'Distribution', 'Maprotiline, St. John''s Wort, Doxepin, Isocarboxazid, Viloxazine, Amoxapine, Esketamine, Phenelzine, Desipramine, Tranylcypromine, Trimipramine, More', 'Methoxyflurane', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/64897/', '[1] Juarez-Olguin H, Jung-Cook H, Flores-Perez J, Asseff IL "Clinical Evidence of an Interaction Between Imipramine and Acetylsalicylic Acid on Protein Binding in Depressed Patients." Clin Neuropharmacol 25 (2002): 32-36', 1767369485);
