INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (64867, 'Choline salicylate', 'Plazomicin', 'Moderate', 'The nephrotoxic effect of aminoglycosides may be potentiated by nonsteroidal anti-inflammatory drugs (NSAIDs), particularly if the latter had been given in high dosages for prolonged periods. Animal models suggest that renal prostaglandins may play a role in maintaining normal renal blood flow and glomerular filtration rate during the development of aminoglycoside nephrotoxicity, thus inhibition of their production by NSAIDs may worsen the renal damage. In premature infants, NSAIDs may increase the plasma concentrations of aminoglycosides. The proposed mechanism is decreased aminoglycoside clearance due to NSAID reduction of glomerular filtration rate.', NULL, 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy.', NULL, 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy. If concomitant administration is necessary, hydration status as well as renal and vestibular functions should be closely monitored.', 'Synergism', 'Neomycin', 'Acetaminophen, Ziconotide', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/64953/', '[1] Gagliardi L "Possible indomethacin-aminoglycoside interaction in preterm infants." J Pediatr 107 (1985): 991-2[2] Farag MM, Mikhail MR, Abdel-Meguid E, Abdel-Tawab S "Assessment of gentamicin-induced nephrotoxicity in rats treated with low doses of ibuprofen and diclofenac sodium." Clin Sci 91 (1996): 187-91[3] Scott CS, RetschBogart GZ, Henry MM "Renal failure and vestibular toxicity in an adolescent with cystic fibrosis receiving gentamicin and standard-dose ibuprofen." Pediat Pulm 31 (2001): 314-6[4] Kovesi TA, Swartz R, MacDonald N "Transient renal failure due to simultaneous ibuprofen and aminoglycoside therapy in children with cystic fibrosis." N Engl J Med 338 (1998): 65-6[5] Assael BM, Chiabrando C, Gagliardi L, Noseda A, Bamonte F, Salmona M "Prostaglandins and aminoglycoside nephrotoxicity." Toxicol Appl Pharmacol 78 (1985): 386-94[6] Zarfin Y, Koren G, Maresky D, et al "Clinical and laboratory observations: possible indomethacin-aminoglycoside interaction in preterm infants." J Pediatr 106 (1985): 511-3', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (64868, 'Choline salicylate', 'Potassium citrate', 'Moderate', 'Agents that cause urinary alkalinization can reduce serum salicylate concentrations in patients receiving anti-inflammatory dosages of aspirin or other salicylates. The mechanism involves reduction in salicylate renal tubular reabsorption due to increased urinary pH, resulting in increased renal salicylate clearance especially above urine pH of 7. This interaction is sometimes exploited in the treatment of salicylate toxicity.', NULL, 'Patients treated chronically with urinary alkalinizers and large doses of salicylates (i.', NULL, 'Patients treated chronically with urinary alkalinizers and large doses of salicylates (i.e. 3 g/day or more) should be monitored for potentially diminished or inadequate analgesic and anti-inflammatory effects, and the salicylate dosage adjusted if necessary.', 'Excretion', 'Potassium chloride, Potassium bicarbonate, Potassium gluconate', 'Acetaminophen, Ziconotide, Methoxyflurane', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/64954/', '[1] Prescott LF, Balali-Mood M, Critchley JA, Johnstone AF, Proudfoot AT "Diuresis or urinary alkalinisation for salicylate poisoning?" Br Med J (Clin Res Ed) 285 (1982): 1383-6[2] Brouwers JRBJ, Desmet PAGM "Pharmacokinetic-pharmacodynamic drug interactions with nonsteroidal anti-inflammatory drugs." Clin Pharmacokinet 27 (1994): 462-85[3] Berg KJ "Acute effects of acetylsalicylic acid in patients with chronic renal insufficiency." Eur J Clin Pharmacol 11 (1977): 111-6[4] Balali-Mood M, Prescott LF "Failure of alkaline diuresis to enhance diflunisal elimination." Br J Clin Pharmacol 10