INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (64898, 'Choline salicylate', 'Urokinase', 'Moderate', 'Drugs that inhibit platelet function may increase the risk of bleeding when administered prior to, during, or after thrombolytic therapy.', NULL, 'Careful monitoring for signs of bleeding, particularly at arterial puncture wounds, is recommended when thrombolytics are used concurrently or sequentially with antiplatelet agents other than aspirin.', NULL, 'Careful monitoring for signs of bleeding, particularly at arterial puncture wounds, is recommended when thrombolytics are used concurrently or sequentially with antiplatelet agents other than aspirin. Some authorities recommend avoiding the initiation of platelet aggregation inhibitors within the first 24 hours following thrombolysis treatment.', 'Synergism', 'Dipyridamole, Caplacizumab, Cilostazol, Defibrotide, Antithrombin III human, Protein C, Acenocoumarol, Selexipag, Vorapaxar, Cangrelor, Ticlopidine', 'Acetaminophen, Ziconotide, Methoxyflurane', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/64984/', '[1] Harder S, Klinkhardt U "Thrombolytics: drug interactions of clinical significance." Drug Saf 23 (2000): 391-9[2] "Product Information. TNKase (tenecteplase)" Genentech, South San Francisco, CA.[3] "Product Information. Abbokinase (urokinase)." Abbott Pharmaceutical, Abbott Park, IL.[4] "Product Information. Retavase (reteplase)." Boehringer Mannheim, Gaithersburg, MD.[5] "Product Information. Ticlid (ticlopidine)." Syntex Laboratories Inc, Palo Alto, CA.[6] "Product Information. Activase (alteplase)." Genentech, South San Francisco, CA.[7] Hirsch J, Dalen J, Guyatt G, American College of Chest Physicians "The sixth (2000) ACCP guidelines for antithrombotic therapy for prevention and treatment of thrombosis. American College of Physicians." Chest 119(1 Suppl) (2001): 1S-2S[8] "Product Information. Streptase (streptokinase)." Astra USA, Westborough, MA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (64899, 'Choline salicylate', 'Valsartan', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may attenuate the antihypertensive effects of angiotensin II receptor antagonists. The proposed mechanism is NSAID-induced inhibition of renal prostaglandin synthesis, which results in unopposed pressor activity producing hypertension. In addition, NSAIDs can cause fluid retention, which also affects blood pressure. Concomitant use of NSAIDs and angiotensin II receptor antagonists may cause deterioration in renal function, particularly in patients who are elderly or volume-depleted (including those on diuretic therapy) or have compromised renal function.', NULL, 'Patients receiving angiotensin II receptor antagonists who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID.', NULL, 'Patients receiving angiotensin II receptor antagonists who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID. Renal function should also be evaluated periodically during prolonged coadministration. The interaction is not expected to occur with low doses (e.g., low-dose aspirin) or intermittent short-term administration of NSAIDs.', 'Antagonism', 'Hydrochlorothiazide, Sacubitril, Atorvastatin, Simvastatin, Lovastatin, Rosuvastatin, Pravastatin, Ezetimibe, Bempedoic acid, Indapamide, Fenofibrate', 'Ziconotide, Methoxyflurane', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/64985/', '[1] Radack KL, Deck CC, Bloomfield SS "Ibuprofen interferes with the efficacy of antihypertensive drugs: a randomized, double-blind, placebo-controlled trial of ibuprofen compared with acetaminophen." Ann Intern Med 107 (1987): 628-35[2] "Multum Information Services, Inc. Expert Review Panel"[3] "Product Information. Toradol (ketorolac)." Roche Laboratories, Nutley, NJ.[4] "Product Information. Celebrex (celecoxib)." Searle, Chicago, IL.[5] "Product Information. Diovan (valsartan)." Novartis Pharmaceuticals, East Hanover, NJ.[6] "Product Information. Cozaar (losartan)." Merck & Co, Inc, West Point, PA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (64900, 'Choline salicylate', 'Varicella Zoster Vaccine (Recombinant)', 'Moderate', 'In pediatric patients, use of varicella virus vaccine during salicylate or salicylate-containing therapy may pose a theoretical risk of Reye''s syndrome based on known association of the syndrome with salicylates and natural varicella infection.', NULL, 'Vaccine recipients less than 18 years of age should avoid the use of salicylates for six weeks after immunization with varicella virus vaccine.', NULL, 'Vaccine recipients less than 18 years of age should avoid the use of salicylates for six weeks after immunization with varicella virus vaccine.', 'Others', 'Salicylic acid, Acetaminophen, Ziconotide, Methoxyflurane', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/64986/', '[1] "Product Information. Dipentum (olsalazine)." Meda Pharmaceuticals, Somerset, NJ.[2] "Product Information. Varivax (varicella virus vaccine live)." Merck & Co, Inc, West Point, PA.', 1767369485);
