INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (129029, 'Mifepristone', 'Lenvatinib', 'Major', 'Mifepristone may prolong the QTc interval in a dose-related manner. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'When mifepristone is used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, coadministration with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'When mifepristone is used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, coadministration with other drugs that can prolong the QT interval should generally be avoided. Caution is recommended if no alternatives exist and concomitant use is required. Serum potassium should be assessed prior to starting mifepristone and 1 to 2 weeks following initiation of therapy or an increase in dosage, and periodically as needed. Hypokalemia must be corrected prior to initiation of mifepristone. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hypokalemia such as fatigue, weakness, myalgia, muscle cramps, numbness, tingling, abdominal pain, constipation, palpitation, and irregular heart rhythm.', 'Synergism', 'Ulipristal, Danazol, Ospemifene, Raloxifene, Bazedoxifene, Prasterone', 'Regorafenib, Alpelisib, Trametinib, Erlotinib, Erdafitinib, Afatinib, Dacomitinib, Tivozanib, Alectinib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/129183/', '[1] "Pr