INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (129144, 'Lomitapide', 'Letermovir', 'Major', 'Coadministration with potent and moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of lomitapide, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of lomitapide with potent or moderate inhibitors of CYP450 3A4 is considered contraindicated.', NULL, 'Concomitant use of lomitapide with potent or moderate inhibitors of CYP450 3A4 is considered contraindicated. Some authorities consider concomitant administration of lomitapide and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Fenofibric acid, Mipomersen, Evinacumab, Evolocumab, Dextrothyroxine, Probucol, Cholestyramine, Ezetimibe, Clofibrate, Colestipol, Niacin, More', 'Paritaprevir, Glecaprevir, Simeprevir, Cabotegravir, Zanamivir, Valaciclovir, Velpatasvir, Etravirine, Oseltamivir, Acyclovir, Brincidofovir, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/129298/', '[1] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[2] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.', 1767369485);
