INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (129149, 'Lurasidone', 'Letermovir', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of lurasidone and its active metabolite, both of which are primarily metabolized by the isoenzyme.', NULL, 'Lurasidone dosage should start at 20 mg/day and not exceed 80 mg/day when coadministered with moderate CYP450 3A4 inhibitors.', NULL, 'Lurasidone dosage should start at 20 mg/day and not exceed 80 mg/day when coadministered with moderate CYP450 3A4 inhibitors. If a moderate CYP450 3A4 inhibitor is added to existing lurasidone therapy, the lurasidone dosage should be reduced by half. Patients should be monitored for potentially increased adverse effects such as dizziness, drowsiness, dry mouth, increased appetite, weight gain, extrapyramidal symptoms, tardive dyskinesia, hyperglycaemia, dyslipidemia, hyperprolactinemia (galactorrhea, amenorrhea, gynecomastia), orthostatic hypotension, cognitive and motor impairment, seizures, dysphagia, and heat-related illnesses due to disruption of body temperature regulation.', 'Metabolism', 'Fluphenazine, Thiothixene, Risperidone, Amisulpride, Prochlorperazine, Promazine, Trifluoperazine, Paliperidone, Chlorpromazine, Mesoridazine, Methotrimeprazine, More', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/129303/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Latuda (lurasidone)." Sunovion Pharmaceuticals Inc, Marlbo