INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (129196, 'Pitavastatin', 'Letermovir', 'Major', 'Coadministration with letermovir may significantly increase the plasma concentrations of pitavastatin. The proposed mechanism is letermovir inhibition of the OATP1B1/3-mediated hepatic uptake of pitavastatin. Although the interaction has not been studied, substantial increases in pitavastatin exposure are expected based on known pharmacokinetics of the drugs. Additional use of cyclosporine is likely to further increase the magnitude of interaction, since it is a strong inhibitor of OATP 1B1 and 1B3.', NULL, 'Concomitant use of pitavastatin with letermovir should generally be avoided.', NULL, 'Concomitant use of pitavastatin with letermovir should generally be avoided. The combination is considered contraindicated when coadministered with cyclosporine. Fluvastatin or pravastatin may be suitable alternatives, although lower dosages of the statin are recommended in combination with letermovir and/or cyclosporine. Please refer to the statin prescribing information for specific statin dosing recommendations. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise, and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'Fenofibric acid, Mipomersen, Evinacumab, Evolocumab, Dextrothyroxine, Probucol, Cholestyramine, Ezetimibe, Clofibrate, Colestipol, Niacin, More', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/129350/', '[1] "Product Information. Prevymis (letermovir)." Merck & Company Inc, Whitehouse Station, NJ.', 1767369485);
