INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (129240, 'Sirolimus', 'Letermovir', 'Major', 'Coadministration with letermovir may significantly increase the plasma concentrations of sirolimus. The proposed mechanism is letermovir inhibition of intestinal and hepatic CYP450 3A4, the isoenzyme responsible for the metabolic clearance of sirolimus. Enhanced sirolimus oral bioavailability due to inhibition of intestinal P-glycoprotein (P-gp) efflux transporter may also contribute.', NULL, 'Caution is advised when sirolimus is used with letermovir.', NULL, 'Caution is advised when sirolimus is used with letermovir. Sirolimus blood levels and renal function should be checked frequently and the dosage adjusted accordingly, particularly following initiation or discontinuation of letermovir. When letermovir is coadministered with cyclosporine, please refer to the sirolimus prescribing information for specific sirolimus dosing recommendations. Patients should be advised to seek medical attention if they experience adverse effects such as fever, infection, diarrhea, stomatitis, decreased urination, angioedema, peripheral edema, shortness of breath, difficulty breathing, and changes in motor function or mental status.', 'Metabolism, Absorption', 'Alteplase, Ascorbic acid, Vitamin A, Potassium Iodide, Acetylcysteine, Nandrolone, Heparin, Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, More', 'Cabotegravir, Maraviroc, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, Oseltamivir, Brincidofovir, Stavudine, Elbasvir, Bictegravir, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/129394/', '[1] "Product Information. Prevymis (letermovir)." Merck & Company Inc, Whitehouse Station, NJ.[2] "Product Information. Rapamune (sirolimus)" Wyeth-Ayerst Laboratories, Philadelphia, PA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (129241, 'Solifenacin', 'Letermovir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of solifenacin, which is primarily metabolized by the isoenzyme.', NULL, 'Pharmacologic response to solifenacin should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the solifenacin dosage adjusted if necessary.', NULL, 'Pharmacologic response to solifenacin should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the solifenacin dosage adjusted if necessary. Patients should be advised to contact their physician if they experience undue adverse effects of solifenacin such as severe abdominal pain or constipation for 3 or more days. Patients should seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism', 'Apomorphine, Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Trospium, Acetohydroxamic acid, Yohimbine, Flavoxate, Papaverine, Phenyl salicylate, More', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Dolutegravir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/129395/', '[1] "Product Information. VESIcare (solifenacin)." GlaxoSmithKline, Research Triangle Park, NC.[2] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (129242, 'Sonidegib', 'Letermovir', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of sonidegib, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of sonidegib with moderate CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of sonidegib with moderate CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends limiting use of the moderate CYP450 3A4 inhibitor to less than 14 days and monitoring for adverse reactions, particularly musculoskeletal toxicity.', 'Metabolism', 'Pertuzumab, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, More', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/129396/', '[1] "Product Information. Odomzo (sonidegib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Odomzo (sonidegib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
