INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (129247, 'Tadalafil', 'Letermovir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of tadalafil, which is primarily metabolized by the isoenzyme. The possibility of prolonged and/or increased pharmacologic effects of tadalafil should be considered.', NULL, 'Caution is advised if tadalafil is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if tadalafil is prescribed with CYP450 3A4 inhibitors. Dosage adjustments may be appropriate for tadalafil whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy based on efficacy and side effects. Tadalafil labeling recommends that the dosage not exceed 10 mg once every 72 hours in patients treated concomitantly with a potent CYP450 3A4 inhibitor, such as erythromycin, itraconazole, ketoconazole, protease inhibitors, and nefazodone. Patients should be advised to promptly notify their physician if they experience potential symptoms of PDE5 inhibitor toxicity such as pain or tightness in the chest or jaw, irregular heartbeat, nausea, shortness of breath, visual disturbances, syncope, or prolonged erection (greater than 4 hours).', 'Metabolism', 'Apomorphine, Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Trospium, Acetohydroxamic acid, Yohimbine, Flavoxate, Papaverine, Phenyl salicylate, More', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, Oseltamivir, Brincidofovir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/129401/', '[1] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company, Indianapolis, IN.[2] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company, Indianapolis, IN.[3] "Product Information. Adcirca (tadalafil)." United Therapeutics Corporation, Silver Spring, MD.', 1767369485);
