INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (129830, 'Flecainide', 'Levobunolol (ophthalmic)', 'Moderate', 'Beta-blockers and flecainide may have additive negative inotropic effects. Areas under the curve were increased for both drugs and negative inotropic effects occurred when flecainide and propranolol were given to normal subjects. A case of bradycardia, atrioventricular block and cardiac arrest has been reported after sotalol was added to flecainide.', NULL, 'Careful monitoring of the patient''s hemodynamic status is recommended during concomitant administration.', NULL, 'Careful monitoring of the patient''s hemodynamic status is recommended during concomitant administration. The same precaution should be observed when beta blocker ophthalmic solutions are used, since they are systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels. Patients should be advised to promptly report symptoms such as dizziness, slow or irregular heartbeats, syncope, or palpitations.', 'Synergism', 'Quinidine, Lidocaine, Propafenone, Procainamide, Mexiletine, Dofetilide, Bretylium, Moricizine, Ibutilide', 'Apraclonidine, Methazolamide, Neostigmine, Pilocarpine, Physostigmine, Acetylcholine, Diclofenamide, Bimatoprost, Travoprost, Acetazolamide, Brinzolamide, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/129985/', '[1] Lewis GP, Holtzman JL "Interaction of flecainide with digoxin and propranolol." Am J Cardiol 53 (1984): b52-7[2] Minish T, Herd A "Symptomatic bradycardia secondary to interaction between topical timolol maleate, verapamil, and flecainide: a case report." J Emerg Med 22 (2002): 247-9[3] H