INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (129865, 'Linezolid', 'Levobunolol (ophthalmic)', 'Moderate', 'Sympathetic ganglion-blocking or catecholamine-depleting agents such as guanethidine, reserpine, and monoamine oxidase (MAO) inhibitors may potentiate the pharmacologic effects of beta-blockers, which are thought to competitively antagonize catecholamines at cardiac and other peripheral adrenergic neurons. Combining these medications may increase the risk of hypotension, orthostasis, bradycardia, and heart failure due to excessive reduction of sympathetic activity.', NULL, 'Caution is advised if beta-blockers, including ophthalmic formulations, are prescribed in combination with sympathetic ganglion-blocking or catecholamine-depleting agents.', NULL, 'Caution is advised if beta-blockers, including ophthalmic formulations, are prescribed in combination with sympathetic ganglion-blocking or catecholamine-depleting agents. Patients should contact their doctor if they experience dizziness, lightheadedness, syncope, bradycardia, shortness of breath, difficulty breathing, edema, and/or chest pain.', 'Synergism', 'Tinidazole, Bacitracin, Polymyxin B, Oritavancin, Daptomycin, Vancomycin, Metronidazole, Telavancin, Methenamine, Spectinomycin, Lefamulin, More', 'Methazolamide, Neostigmine, Pilocarpine, Physostigmine, Acetylcholine, Diclofenamide, Bimatoprost, Travoprost, Brinzolamide, Brimonidine, Levobunolol', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/130021/', '[1] "Product Information. Marplan (isocarboxazid)" Roche Laboratories, Nutley, NJ.[2] "Product Information. Matulane (procarbazine)." Roche Laboratories, Nutley, NJ.[3] Kronig MH, Roose SP, Walsh BT, Woodring S, Glassman AH "Blood pressure effects of phenelzine." J Clin Psychopharmacol 3 (1983): 307-10[4] "Product Information. Nardil (phenelzine)." Parke-Dvis, Morris Plains, NJ.[5] Golwyn DH, Sevlie CP "Monoamine oxidase inhibitor hypertensive crisis headache and orthostatic hypotension." J Clin Psychopharmacol 13 (1993): 77-8[6] "Product Information. Parnate (tranylcypromine)." SmithKline Beecham, Philadelphia, PA.[7] De Vita VT, Hahn MA, Oliverio VT "Monoamine oxidase inhibition by a new carcinostatic agent, n-isopropyl-a-(2-methylhydrazino)-p-toluamide (MIH). (30590)." Proc Soc Exp Biol Med 120 (1965): 561-5[8] Pettinger WA, Soyangco FG, Oates JA "Inhibition of monoamine oxidase in man by furazolidone." Clin Pharmacol Ther 9 (1968): 442-7[9] "Product Information. Hylorel (guanadrel)." Rhone-Poulenc Rorer, Collegeville, PA.[10] Reggev A, Vollhardt BR "Bradycardia induced by an interaction between phenelzine and beta blockers." Psychosomatics 30 (1989): 106-8[11] Goldberg LI "Monoamine oxidase inhibitors: adverse reactions and possible mechanisms." JAMA 190 (1964): 456-62[12] "Product Information. Toprol-XL (metoprolol)" Astra-Zeneca Pharmaceuticals, Wilmington, DE.[13] Kraus RP "Pindolol augmentation of tranylcypromine in psychotic depression." J Clin Psychopharmacol 17 (1997): 225-6', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (129866, 'Mephentermine', 'Levobunolol (ophthalmic)', 'Moderate', 'Beta-blockers may antagonize the cardiostimulatory effects of pressor agents by blocking beta-1 adrenergic receptors in the heart. Vasopressors may be less effective if the patient is receiving, or has recently received, a beta-blocking drug. In addition, peripheral vascular resistance may increase due to unopposed alpha-adrenergic effects of pressor agents in the presence of beta-blockade. Theoretically, the interaction may also occur with beta-blocker ophthalmic preparations, since they may be systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels.', NULL, 'No specific intervention is necessary, but clinicians should be alert to the potential for diminished cardiac response when pressor agents are used in patients treated with beta-blockers, including ophthalmic formulations.', NULL, 'No specific intervention is necessary, but clinicians should be alert to the potential for diminished cardiac response when pressor agents are used in patients treated with beta-blockers, including ophthalmic formulations. A beta-blocker such as propranolol may be used to treat cardiac arrhythmias that occur during administration of pressor agents like mephentermine, metaraminol, norepinephrine, or phenylephrine.', 'Antagonism', 'Latanoprost, Apraclonidine, Methazolamide, Neostigmine, Pilocarpine, Clonidine, Physostigmine, Acetylcholine, Diclofenamide, Bimatoprost, Travoprost, More', 'Droxidopa, Amrinone, Angiotensin II, Milrinone, Methoxamine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/130022/