INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1362821, 'Fostamatinib', 'Dexamethasone', 'Moderate', 'Coadministration of fostamatinib with inducers of CYP450 3A4 may decrease exposure to the active metabolite known as R406, the predominant moiety in the systemic circulation following fostamatinib administration. Fostamatinib is metabolized in the gut by alkaline phosphatase to R406, which then undergoes oxidation via CYP450 3A4 and glucuronidation via UGT1A9.', 'DDInter', 1767317475, 'The potential for diminished pharmacologic effects of fostamatinib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of fostamatinib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1362822, 'Fostemsavir', 'Dexamethasone', 'Minor', 'Coadministration of fostemsavir with moderate or weak CYP450 3A4 inducers may decrease the plasma concentrations of temsavir, the active moiety of fostemsavir. According to the prescribing information, temsavir is a substrate of CYP450 3A4, esterases, P-glycoprotein (P-gp), and breast cancer resistance protein (BCRP).', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1362823, 'Furosemide', 'Dexamethasone', 'Moderate', 'The concomitant use of corticosteroids and agents that deplete potassium (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may result in increased risk of hypokalemia. Corticosteroids can produce hypokalemia and other electrolyte disturbances via mineralocorticoid effects, the degree of which varies with the agent and route of administration (i.e. systemic vs. local).', 'DDInter', 1767317475, 'Patients receiving potassium-depleting agents with corticosteroids should be monitored closely for development of hypokalemia, particularly if fludrocortisone or large doses of another corticosteroid or adrenocorticotropic agent is given. Potassium supplementation may be necessary. Patients should be advised to notify their physician if they experience signs of electrolyte disturbances such as weakness, lethargy, and muscle pains or cramps.', 'Synergism', 'Patients receiving potassium-depleting agents with corticosteroids should be monitored closely for development of hypokalemia, particularly if fludrocortisone or large doses of another corticosteroid or adrenocorticotropic agent is given.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1362824, 'Gallium chloride Ga-67', 'Dexamethasone', 'Moderate', 'The concomitant use of glucocorticoids may interfere with uptake of gallium citrate Ga-67 by brain tumors or abscesses. In addition, prednisone may cause thymic uptake of Ga-67.', 'DDInter', 1767317475, 'Clinicians should be aware of possible interference with diagnostic tests in patients who are taking these medications.', 'Distribution', 'Clinicians should be aware of possible interference with diagnostic tests in patients who are taking these medications.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1362825, 'Gatifloxacin', 'Dexamethasone', 'Major', 'Concomitant administration of corticosteroids may potentiate the risk of tendinitis and tendon rupture associated with fluoroquinolone treatment. The mechanism is unknown. Tendinitis and tendon rupture have most frequently involved the Achilles tendon, although cases involving the rotator cuff (the shoulder), the hand, the biceps, and the thumb have also been reported. Some have required surgical repair or resulted in prolonged disability. Tendon rupture can occur during or up to several months after completion of fluoroquinolone therapy.', 'DDInter', 1767317475, 'Caution is recommended if fluoroquinolones are prescribed in combination with corticosteroids, particularly in patients with other concomitant risk factors (e.g., age over 60 years; recipient of kidney, heart, and/or lung transplant). Patients should be advised to stop taking the fluoroquinolone, avoid exercise and use of the affected area, and promptly contact their physician if they experience pain, swelling, or inflammation of a tendon.', 'Synergism', 'Caution is recommended if fluoroquinolones are prescribed in combination with corticosteroids, particularly in patients with other concomitant risk factors (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1362826, 'Gefitinib', 'Dexamethasone', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of gefitinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Pharmacologic response to gefitinib should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy. In patients receiving potent inducers such as rifampin or phenytoin, gefitinib product labeling states that a dosage increase to 500 mg daily should be considered in the absence of severe adverse reactions, and additional dosage adjustments be made based on clinical response. Gefitinib dosage should be decreased accordingly if these agents are discontinued.', 'Metabolism', 'Pharmacologic response to gefitinib should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
