INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1362828, 'Gilteritinib', 'Dexamethasone', 'Moderate', 'Coadministration with inducers of CYP450 3A4 and/or P-glycoprotein may decrease the plasma concentrations of gilteritinib, which is a substrate of both the isoenzyme and the efflux transporter.', 'DDInter', 1767317475, 'The potential for diminished pharmacologic effects of gilteritinib should be considered during coadministration with CYP450 3A4 and/or P-gp inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of gilteritinib should be considered during coadministration with CYP450 3A4 and/or P-gp inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1362829, 'Glasdegib', 'Dexamethasone', 'Major', 'Coadministration with moderate inducers of CYP450 3A4 may decrease the plasma concentrations of glasdegib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of glasdegib with moderate CYP450 3A4 inducers should generally be avoided. If concomitant use cannot be avoided, the glasdegib dose should be increased as tolerated from 100 mg once a day to 200 mg once a day or from 50 mg once a day to 100 mg once a day. Once the moderate CYP450 3A4 inducer has been discontinued for 7 days, the glasdegib dose should return to the dose taken prior to initiating the moderate CYP450 3A4 inducer.', 'Metabolism', 'Concomitant use of glasdegib with moderate CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1362830, 'Glimepiride', 'Dexamethasone', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 1767317475, 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Antagonism', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
