INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1362847, 'Ibrutinib', 'Dexamethasone', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ibrutinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'The potential for diminished therapeutic effects of ibrutinib should be considered when prescribed in combination with CYP450 3A4 inducers. Patients should be closely monitored, and the dosage of ibrutinib adjusted as necessary in accordance with product labeling.', 'Metabolism', 'The potential for diminished therapeutic effects of ibrutinib should be considered when prescribed in combination with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1362848, 'Idelalisib', 'Dexamethasone', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of idelalisib, which is a substrate of the isoenzyme.', 'DDInter', 1767317475, 'Patients receiving idelalisib with CYP450 3A4 inducers should be monitored for potentially diminished pharmacologic response to idelalisib. Concomitant use should be avoided if an interaction is suspected.', 'Metabolism', 'Patients receiving idelalisib with CYP450 3A4 inducers should be monitored for potentially diminished pharmacologic response to idelalisib.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1362849, 'Imatinib', 'Dexamethasone', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of imatinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'The potential for diminished pharmacologic effects of imatinib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of imatinib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1362850, 'Indacaterol', 'Dexamethasone', 'Minor', 'Although they are often combined in clinical practice, the concomitant use of beta-2 adrenergic agonists and corticosteroids may result in additive hypokalemic effects. Since beta-2 agonists can sometimes cause QT interval prolongation, the development of hypokalemia may potentiate the risk of ventricular arrhythmias including torsade de pointes. However, clinical data are limited, and the potential significance is unknown. Patients who are receiving systemic or nebulized formulations of beta-2 agonists, high dosages of inhaled beta-2 agonists, or systemic corticosteroid therapy may be at a greater risk of developing hypokalemia.', 'DDInter', 1767317475, '-', 'Synergism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1362851, 'Indapamide', 'Dexamethasone', 'Moderate', 'The concomitant use of corticosteroids and agents that deplete potassium (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may result in increased risk of hypokalemia. Corticosteroids can produce hypokalemia and other electrolyte disturbances via mineralocorticoid effects, the degree of which varies with the agent and route of administration (i.e. systemic vs. local).', 'DDInter', 1767317475, 'Patients receiving potassium-depleting agents with corticosteroids should be monitored closely for development of hypokalemia, particularly if fludrocortisone or large doses of another corticosteroid or adrenocorticotropic agent is given. Potassium supplementation may be necessary. Patients should be advised to notify their physician if they experience signs of electrolyte disturbances such as weakness, lethargy, and muscle pains or cramps.', 'Synergism', 'Patients receiving potassium-depleting agents with corticosteroids should be monitored closely for development of hypokalemia, particularly if fludrocortisone or large doses of another corticosteroid or adrenocorticotropic agent is given.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1362852, 'Indinavir', 'Dexamethasone', 'Moderate', 'Coadministration with protease inhibitors may increase the plasma concentration and pharmacologic effects of dexamethasone. The proposed mechanism is protease inhibitor-mediated inhibition of CYP450 3A4, the isoenzyme involved in the metabolic clearance of dexamethasone. Corticosteroids such as dexamethasone may cause hypokalemia and potentiate the risk of QT and/or PR interval prolongation associated with the use of certain protease inhibitors such as atazanavir, lopinavir-ritonavir, and saquinavir-ritonavir. The risk of torsade de pointes arrhythmia, bradycardia, and heart block may be increased.', 'DDInter', 1767317475, 'Caution is advised if dexamethasone must be used concomitantly with protease inhibitors or cobicistat. Some authorities advise against concomitant use unless the potential benefit outweighs the risk. Adrenal function should be monitored regularly during chronic use of these agents, and dexamethasone dosage adjusted as necessary. Patients should be monitored for symptoms of hypercorticism (e.g., acne, easy bruising, moon face, edema, hirsutism, buffalo hump, skin striae, glucose intolerance, and irregular menstruations), immunosuppression, and osteoporosis. In addition, it may be appropriate to monitor patients for potentially reduced antiretroviral response following initiation or any dosage increase of dexamethasone. Serum potassium and ECG monitoring should also be considered during coadministration of dexamethasone with certain protease inhibitors in accordance with the product labeling.', 'Metabolism', 'Caution is advised if dexamethasone must be used concomitantly with protease inhibitors or cobicistat.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1362853, 'Indium In-111 oxyquinoline', 'Dexamethasone', 'Moderate', 'Certain medications may decrease chemotaxis and cause false-negative results with Indium In-111-labeled leukocyte studies.', 'DDInter', 1767317475, 'Clinicians should be aware of possible diagnostic interference in patients who are on long-term antibiotic treatment, corticosteroids, interleukin-2, parenteral nutrition, or high doses or supratherapeutic concentrations of lidocaine or procainamide.', 'Others', 'Clinicians should be aware of possible diagnostic interference in patients who are on long-term antibiotic treatment, corticosteroids, interleukin-2, parenteral nutrition, or high doses or supratherapeutic concentrations of lidocaine or procainamide.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1362854, 'Indomethacin', 'Dexamethasone', 'Moderate', 'The combined use of corticosteroids and nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the potential for serious gastrointestinal (GI) toxicity, including inflammation, bleeding, ulceration, and perforation.', 'DDInter', 1767317475, 'Caution is advised if corticosteroids and NSAIDs are used together, especially in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly and debilitated patients. During concomitant therapy, patients should be advised to take the medications with food and to immediately report signs and symptoms of GI ulceration and bleeding such as severe abdominal pain, dizziness, lightheadedness, and the appearance of black, tarry stools. The selective use of prophylactic anti-ulcer therapy (e.g., antacids, H2-antagonists) may be considered.', 'Synergism', 'Caution is advised if corticosteroids and NSAIDs are used together, especially in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly and debilitated patients.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1362855, 'Infliximab', 'Dexamethasone', 'Major', 'The use of tumor necrosis factor (TNF) blockers with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Serious infections and sepsis, including fatalities, have been reported with the use of TNF blockers, particularly in patients on concomitant immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids. Concomitant use of TNF blockers with other immunosuppressants such as azathioprine or mercaptopurine may also increase the risk of a rare and often fatal cancer of white blood cells known as hepatosplenic T-Cell lymphoma (HSTCL).', 'DDInter', 1767317475, 'Patients receiving a TNF blocker alone or with other immunosuppressive or myelosuppressive agents should be monitored closely for the development of infections. TNF blocker therapy should be discontinued if a serious infection or sepsis occurs. Close monitoring for signs and symptoms of HSTCL (e.g., splenomegaly, hepatomegaly, abdominal pain, persistent fever, night sweats, weight loss) is also recommended during use of TNF blockers, particularly in combination with other immunosuppressants such as azathioprine and mercaptopurine.', 'Synergism', 'Patients receiving a TNF blocker alone or with other immunosuppressive or myelosuppressive agents should be monitored closely for the development of infections.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1362856, 'Influenza A virus A/California/7/2009 (H1N1)-like antigen (propiolactone inactivated)', 'Dexamethasone', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317475, 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients. For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease. For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.', 'Antagonism', 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.', NULL, NULL, 'Moderate', 'pharmacodynamic');
