INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1362872, 'Iohexol', 'Dexamethasone', 'Major', 'Coadministration of corticosteroids with iodinated contrast media by the intrathecal route may be associated with a high incidence of radiographic arachnoiditis. Meningitis and seizures may also occur.', 'DDInter', 1767317475, 'Intrathecal coadministration of corticosteroids with iodinated contrast media is considered contraindicated.', 'Others', 'Intrathecal coadministration of corticosteroids with iodinated contrast media is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
