INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1362952, 'Dexamethasone', 'Mirabegron', 'Minor', 'Coadministration with inducers of CYP450 3A4 and/or P-glycoprotein may decrease the plasma concentrations of mirabegron, which has been shown in vitro to be a substrate of the isoenzyme and efflux transporter. No dosage adjustment is recommended when mirabegron is administered in combination with rifampin and probably other CYP450 3A4/P-gp inducers.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1362953, 'Dexamethasone', 'Mirtazapine', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of mirtazapine, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317475, 'The possibility of diminished therapeutic response to mirtazapine should be considered during coadministration with CYP450 3A4 inducers, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, rifampin, and St. John''s wort. Pharmacologic response to mirtazapine should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the dosage adjusted as necessary.', 'Metabolism', 'The possibility of diminished therapeutic response to mirtazapine should be considered during coadministration with CYP450 3A4 inducers, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, rifampin, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
