INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1362976, 'Dexamethasone', 'Nilotinib', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of nilotinib, which is primarily metabolized by the isoenzyme. In healthy subjects receiving the potent inducer rifampin (600 mg once daily for 12 days), nilotinib systemic exposure (AUC) was decreased approximately 80%.', 'DDInter', 1767317475, 'Concomitant use of nilotinib with potent CYP450 3A4 inducers should generally be avoided due to the potential for reduced efficacy. If coadministration is required, a dosage increase for nilotinib may be necessary depending on patient tolerability. The dosage should be reduced to the indicated dosage following discontinuation of the potent CYP450 3A4 inducer.', 'Metabolism', 'Concomitant use of nilotinib with potent CYP450 3A4 inducers should generally be avoided due to the potential for reduced efficacy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1362977, 'Dexamethasone', 'Nintedanib', 'Major', 'Coadministration with inducers of P-glycoprotein (P-gp) and CYP450 3A4 may decrease the plasma concentrations of nintedanib, which is a substrate of the efflux transporter and a minor substrate of the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of nintedanib with P-gp and CYP450 3A4 inducers should generally be avoided due to the potential for reduced efficacy.', 'Metabolism', 'Concomitant use of nintedanib with P-gp and CYP450 3A4 inducers should generally be avoided due to the potential for reduced efficacy.', NULL, NULL, 'Major', 'pharmacodynamic');
