INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1362979, 'Dexamethasone', 'Nitisinone', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations and therapeutic effects of nitisinone. The proposed mechanism is induction of CYP450 3A4-mediated metabolism of nitisinone, which has been shown in vitro to be metabolized by this isoenzyme.', 'DDInter', 1767317475, 'Until more information is available, caution is advised if nitisinone is used concomitantly with CYP450 3A4 inducers. Dosage adjustments as well as clinical and laboratory monitoring of nitisinone should be considered whenever a CYP450 3A4 inducer is added to or withdrawn from therapy.', 'Metabolism', 'Until more information is available, caution is advised if nitisinone is used concomitantly with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
