INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65118, 'Defibrotide', 'Cilostazol', 'Major', 'Coadministration of defibrotide with antiplatelet agents or other agents that affect coagulation may increase the risk of bleeding. In vitro, defibrotide increased the activity of fibrinolytic enzymes. In a mouse model of thromboembolism, recombinant tissue plasminogen activator (t-PA) potentiated the antithrombotic effect of defibrotide when given intravenously.', NULL, 'Concomitant use of medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with defibrotide.', NULL, 'Concomitant use of medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with defibrotide. Close clinical and laboratory observation for bleeding complications is recommended during therapy. If bleeding develops, defibrotide should be discontinued and treatment/supportive care provided until the bleeding stops.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65205/', '[1] "Product Information. Defitelio (defibrotide)." Jazz Pharmaceuticals, Palo Alto, CA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363022);
