INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65233, 'Cilostazol', 'Levamlodipine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or 2C19 may increase the plasma concentrations of cilostazol and or its pharmacologically active metabolites, which are substrates of these isoenzymes. The possibility of prolonged and/or increased pharmacologic effects of cilostazol should be considered.', NULL, 'Close clinical and laboratory monitoring is advised whenever a CYP450 3A4 and/or 2C19 inhibitor is added to or withdrawn from cilostazol therapy, and the dosage adjusted as necessary.', NULL, 'Close clinical and laboratory monitoring is advised whenever a CYP450 3A4 and/or 2C19 inhibitor is added to or withdrawn from cilostazol therapy, and the dosage adjusted as necessary. Patients should be advised to contact their physician if they experience adverse effects of cilostazol such as dizziness, nausea, diarrhea, bleeding, or irregular heartbeat.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65320/', '[1] Suri A, Bramer SL "Effect of omeprazole on the metabolism of cilostazol." Clin Pharmacokinet 37 (1999): 53-9[2] Herrlin K, Segerdahl M, Gustafsson LL, Kalso E "Methadone, ciprofloxacin, and adverse drug reactions." Lancet 356 (2000): 2069-70[3] "Product Information. Pletal (cilostazol)." Otsuka American Pharmaceuticals, Rockville, MD.[4] Suri A, Forbes WP, Bramer SL "Effects of CYP3A inhibition on the metabolism of cilostazol." Clin Pharmacokinet 37 (1999): 61-8[5] Sriwiriyajan S, Samaeng M, Ridtitid W, Mahatthanatrakul W, Wongnawa M "Pharmacokinetic interactions between ciprofloxacin and itraconazole in healthy male volunt