INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65313, 'Cilostazol', 'Ponatinib', 'Major', 'Coadministration of ponatinib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Treatment with ponatinib is associated with severe, sometimes fatal hemorrhage including cerebral and gastrointestinal hemorrhage.', NULL, 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with ponatinib.', NULL, 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with ponatinib. Close clinical and laboratory observation for bleeding complications is recommended during therapy. The INR should be monitored more frequently during coadministration of warfarin. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65400/', '[1] "Product Information. Iclusig (ponatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.[2] "Product Information. Brukinsa (zanubrutinib)." BeiGene USA, Inc, San Mateo, CA.[3] "Product Information. Iclusig (ponatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65314, 'Cilostazol', 'Posaconazole', 'Major', 'Coadministration with inhibitors of CYP450 3A4 and/or 2C19 may increase the plasma concentrations of cilostazol and or its pharmacologically active metabolites, which are substrates of these isoenzymes. The possibility of prolonged and/or increased pharmacologic effects of cilostazol should be considered.', NULL, 'A 50% dosage reduction of cilostazol (i.', NULL, 'A 50% dosage reduction of cilostazol (i.e., 50 mg twice a day) should be considered when used with potent or moderate CYP450 3A4 and/or 2C19 inhibitors. Close clinical and laboratory monitoring is advised whenever the inhibitor is added to or withdrawn from therapy, and the cilostazol dosage adjusted as necessary. Patients should be advised to contact their physician if they experience adverse effects of cilostazol such as dizziness, nausea, diarrhea, bleeding, or irregular heartbeat.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65401/', '[1] Suri A, Bramer SL "Effect of omeprazole on the metabolism of cilostazol." Clin Pharmacokinet 37 (1999): 53-9[2] Suri A, Forbes WP, Bramer SL "Effects of CYP3A inhibition on the metabolism of cilostazol." Clin Pharmacokinet 37 (1999): 61-8[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Pletal (cilostazol)." Otsuka American Pharmaceuticals, Rockville, MD.[5] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac0580