INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65426, 'Cimetidine', 'Bacampicillin', 'Moderate', 'Coadministration with antacids, H2-receptor antagonists, proton pump inhibitors, or other agents with acid neutralizing or reducing effects may decrease the oral bioavailability and plasma concentrations of ampicillin from bacampicillin. The exact mechanism of interaction is unknown but may be related to degradation of the prodrug when gastric pH is increased.', NULL, 'The possibility of a reduced or subtherapeutic response to bacampicillin should be considered during coadministration with antacids, H2-receptor antagonists, proton pump inhibitors, or other agents that can increase gastric pH.', NULL, 'The possibility of a reduced or subtherapeutic response to bacampicillin should be considered during coadministration with antacids, H2-receptor antagonists, proton pump inhibitors, or other agents that can increase gastric pH. Preferably, these agents should be avoided during therapy with bacampicillin, or an alternative antibiotic be prescribed if these medications cannot be discontinued. Patients treated with antacids (or oral medications that contain antacids such as didanosine buffered tablets or pediatric oral solution) may minimize the effects of the interaction with bacampicillin by separating the times of administration by at least 2 hours.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65513/', '[1] Honig PK, Gillespie BK "Clinical significance of pharmacokinetic drug interactions with over-the-counter (OTC) drugs." Clin Pharmacokinet 35 (1998): 167-71[2] Sommers DK, van Wyk M, Moncrieff J, Schoeman HS "Influence of food and reduced gastric acidity on the bioavailability of bacampicillin and cefuroxime axetil." Br J Clin Pharmacol 18 (1984): 535-9', 1767363022);
