INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65435, 'Cimetidine', 'Cisapride', 'Major', 'Coadministration with cimetidine may increase the plasma concentrations of cisapride. The mechanism is cimetidine inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of cisapride. Conversely, cisapride may accelerate but decrease the extent of gastrointestinal absorption of cimetidine.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of cisapride, concurrent use of cimetidine should be avoided if possible.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of cisapride, concurrent use of cimetidine should be avoided if possible. Other H2-receptor antagonists (e.g., famotidine, nizatidine, ranitidine) have not been shown to significantly inhibit CYP450 3A4 and may be safer alternatives during therapy with cisapride.', 'Metabolism, Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65522/', '[1] Dresser GK, Spence JD, Bailey DG "Pharmacokinetic-pharmacodynamic consequences and clinical relevance of cytochrome P450 3A4 inhibition." Clin Pharmacokinet 38 (2000): 41-57[2] "Product Information. Propulsid (cisapride)." Janssen Pharmaceutica, Titusville, NJ.[3] Lewin MB, Bryant RM, Fenrich AL, Grifka RG "Cisapride-induced long QT interval." J Pediatr 128 (1996): 279-81[4] Michalets EL, Williams CR "Drug interactions with cisapride: clinical implications." Clin Pharmacokinet 39 (2000): 49-75[5] Hill SL, Evangelista JK, Pizzi AM, Mobassaleh M, Fulton DR, Berul CI "Proarrhythmia associated with cisapride in children." Pediatrics 101 (1998): 1053-6[6] Bedford TA, Rowbotham DJ "Cisapride: drug interactions of clinical significance." Drug Saf 15 (1996): 167-75[7] Kirch W, Janisch HD, Ohnhaus EE, van Peer A "Cisapride-cimetidine interaction: enhanced cisapride bioavailability and accelerated cimetidine absorption." Ther Drug Monit 11 (1989): 411-4[8] Desta Z, Soukhova N, Mahal SK, Flockhart DA "Interaction of cisapride with the human cytochrome P450 system: metabolism and inhibition studies." Drug Metab Dispos 28 (2000): 789-800[9] Gray VS "Syncopal episodes associated with cisapride and concurrent drugs." Ann Pharmacother 32 (1998): 648-51[10] Bran S, Murray WA, Hirsch IB, Palmer JP "Long QT syndrome during high-dose cisapride." Arch Intern Med 155 (1995): 765-8[11] Lewin MB, Bryant RM, Fenrich AL, Grifka RG "Cisapride-induced long QT interval." J Pediatr 128 (1996): 279-81[12] Kivisto KT, Lilja TJ, Backman JT, Neuvonen PJ "Repeated consumption of grapefruit juice considerably increases plasma concentrations of cisapride." Clin Pharmacol Ther 66 (1999): 448-53[13] Michalets EL, Williams CR "Drug interactions with cisapride: clinical implications." Clin Pharmacokinet 39 (2000): 49-75[14] Offman EM, Freeman DJ, Dresser GK, Munoz C, Bend JR, Bailey DG "Red wine-cisapride interaction: Comparison with grapefruit juice." Clin Pharmacol Ther 70 (2001): 17-23[15] Bran S, Murray WA, Hirsch IB, Palmer JP "Long QT syndrome during high-dose cisapride." Arch Intern Med 155 (1995): 765-8[16] "Product Information. Propulsid (cisapride)." Janssen Pharmaceutica, Titusville, NJ.[17] Dresser GK, Spence JD, Bailey DG "Pharmacokinetic-pharmacodynamic consequences and clinical relevance of cytochrome P450 3A4 inhibition." Clin Pharmacokinet 38 (2000): 41-57[18] Desta Z, Soukhova N, Mahal SK, Flockhart DA "Interaction of cisapride with the human cytochrome P450 system: metabolism and inhibition studies." Drug Metab Dispos 28 (2000): 789-800[19] Gross AS, Goh YD, Addison RS, Shenfield GM "Influence of grapefruit juice on cisapride pharmacokinetics." Clin Pharmacol Ther 65 (1999): 395-401[20] Hill SL, Evangelista JK, Pizzi AM, Mobassaleh M, Fulton DR, Berul CI "Proarrhythmia associated with cisapride in children." Pediatrics 101 (1998): 1053-6', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65436, 'Cimetidine', 'Citalopram', 'Major', 'Coadministration with CYP450 2C19 inhibitors may increase the plasma concentrations of citalopram, which is partially metabolized by the isoenzyme. Cases of QT interval prolongation and torsade de pointes have been reported during postmarketing use. In general, the risk of ventricular arrhythmia in association with QT prolongation is largely unpredictable, but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances (e.g., hypokalemia, hypomagnesemia).', NULL, 'Given the risk of dose-dependent QT prolongation, citalopram dosage should not exceed 20 mg/day when prescribed in combination with CYP450 2C19 inhibitors such as cimetidine, esomeprazole, etravirine, felbamate, fluconazole, lansoprazole, letrozole, modafinil, omeprazole, oxcarbazepine, ticlopidine, and voriconazole.', NULL, 'Given the risk of dose-dependent QT prolongation, citalopram dosage should not exceed 20 mg/day when prescribed in combination with CYP450 2C19 inhibitors such as cimetidine, esomeprazole, etravirine, felbamate, fluconazole, lansoprazole, letrozole, modafinil, omeprazole, oxcarbazepine, ticlopidine, and voriconazole. Alternatives should be considered when possible, and hypokalemia or hypomagnesemia should be corrected prior to initiation of citalopram treatment and periodically monitored. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter',