INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65450, 'Dacomitinib', 'Cimetidine', 'Moderate', 'INTERVAL: Coadministration with proton pump inhibitors (PPIs) may decrease the plasma concentrations of dacomitinib. The proposed mechanism is a pH-dependent reduction in dissolution or absorption of dacomitinib due to prolonged gastric acid suppression induced by PPIs.', NULL, 'The manufacturer recommends taking dacomitinib at least 6 hours before or 10 hours after H2-receptor antagonists.', NULL, 'The manufacturer recommends taking dacomitinib at least 6 hours before or 10 hours after H2-receptor antagonists. As an alternative, locally-acting antacids may be considered. Concomitant use of dacomitinib with PPIs should generally be avoided.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65537/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Vizimpro (dacomitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767363022);
