INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65453, 'Darolutamide', 'Cimetidine', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may increase the plasma concentrations of darolutamide, which is a substrate of both the isoenzyme and the efflux transporter. Caution is advised when darolutamide is used with CYP450 3A4 or P-gp inhibitors. Patients should be monitored more frequently for adverse effects such as fatigue, hypertension, neutropenia and abnormal liver function tests, and the darolutamide dosage adjusted as necessary in accordance with the product labeling.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65540/', '[1] "Product Information. Nubeqa (darolutamide)." Bayer HealthCare Pharmaceuticals Inc., Whippany, NJ.[2] "Product Information. Nubeqa (darolutamide)." Bayer HealthCare Pharmaceuticals Inc., Whippany, NJ.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65454, 'Dasatinib', 'Cimetidine', 'Major', 'Nonclinical data indicate that the solubility of dasatinib is pH-dependent. Therefore, long-term suppression of gastric acid secretion by H2-receptor antagonists or proton pump inhibitors is likely to reduce dasatinib systemic exposure.', NULL, 'Concomitant use of dasatinib with H2-receptor antagonists or proton pump inhibitors is not recommended.', NULL, 'Concomitant use of dasatinib with H2-receptor antagonists or proton pump inhibitors is not recommended. If gastric acidity management is necessary, antacids should be considered in place of these agents. However, patients treated with dasatinib should avoid taking antacids or oral medications that contain antacids (e.g., didanosine buffered tablets or pediatric oral solution) for at least two hours before and two hours after administration of dasatinib.', 'Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65541/', '[1] "Product Information. Sprycel (dasatinib)." Bristol-Myers Squibb, Princeton, NJ.[2] "Product Information. Sprycel (dasatinib)." Bristol-Myers Squibb, Princeton, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363022);
