INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65470, 'Dofetilide', 'Cimetidine', 'Major', 'Coadministration with inhibitors of organic cation transporter 2 (OCT-2) may increase the plasma concentrations of dofetilide, which is primarily eliminated by glomerular filtration and active tubular secretion via OCT-2, with minor contribution from CYP450 3A4-mediated metabolism.', NULL, 'Given the risk of concentration-dependent QT prolongation, concomitant use of dofetilide with OCT-2 inhibitors is considered contraindicated.', NULL, 'Given the risk of concentration-dependent QT prolongation, concomitant use of dofetilide with OCT-2 inhibitors is considered contraindicated.', 'Excretion', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65557/', '[1] Abel S, Nichols DJ, Brearley CJ, Eve MD "Effect of cimetidine and ranitidine on pharmacokinetics and pharmacodynamics of a single dose of dofetilide." Br J Clin Pharmacol 49 (2000): 64-71[2] "Product Information. Tikosyn (dofetilide)" Pfizer US Pharmaceuticals, Atlanta, GA.[3] Johnson BF, Cheng SL, Venitz J "Transient kinetic and dynamic interactions between verapamil and dofetilide, a class III antiarrhythmic." J Clin Pharmacol 41 (2001): 1248-56[4] "Product Information. Tikosyn (dofetilide)" Pfizer US Pharmaceuticals, Atlanta, GA.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65471, 'Dolasetron', 'Cimetidine', 'Minor', 'Blood levels of hydrodolasetron, the major active metabolite of dolasetron, were increased 24% when given concomitantly with cimetidine. The mechanism is thought to be inhibition of the metabolism of hydrodolasetron by cimetidine. The clinical significance is unknown but may be low because hydrodolasetron is eliminated by multiple routes. The clinician may consider monitoring the patient more closely for dolasetron toxicity (hypotension, dizziness, arrhythmias) if this combination is used.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65558/', '[1] "Product Information. Anzemet (dolasetron)." Hoechst Marion-Roussel Inc, Kansas City, MO.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65472, 'Donepezil', 'Cimetidine', 'Moderate', 'Due to opposing effects, agents that possess anticholinergic activity (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; class IA antiarrhythmics especially disopyramide; carbamazepine; cimetidine; ranitidine) may negate the already small pharmacologic benefits of acetylcholinesterase inhibitors in the treatment of dementia. These agents may also adversely affect elderly patients in general. Clinically significant mental status changes associated with anticholinergic agents can range from mild cognitive impairment to delirium, and patients with Alzheimer''s disease and other dementia are especially sensitive.', NULL, 'Drugs that possess anticholinergic activity should generally be avoided in patients with Alzheimer''s disease or other cognitive impairment, regardless of whether they are receiving an acetylcholinesterase inhibitor.', NULL, 'Drugs that possess anticholinergic activity should generally be avoided in patients with Alzheimer''s disease or other cognitive impairment, regardless of whether they are receiving an acetylcholinesterase inhibitor. For patients requiring treatment to counteract adverse effects of acetylcholinesterase inhibitor therapy (e.g., gastrointestinal intolerance, urinary problems), an agent without anticholinergic properties should be used whenever possible. Otherwise, a dosage reduction, slower titration, or even discontinuation of the acetylcholinesterase inhibitor should be considered. For patients who are already receiving an acetylcholinesterase inhibitor with anticholinergic agents, every attempt should be made to discontinue the latter or substitute them with less anticholinergic alternatives.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65559/', '[1] Katz IR, Sands LP, Bilker W, DiFilippo S, Boyce A, D''Angelo K "Identification of medications that cause cognitive impairment in older people: the case of oxybutynin chloride." J Am Geriatr Soc 46 (1998): 8-13[2] Edwards KR, O''Connor JT "Risk of delirium with concomitant use of tolterodine and acetylcholinesterase inhibitors." J Am Geriatr Soc 50 (2002): 1165-6[3] Carnahan RM, Lund BC, Perry PJ, Chrischilles EA "The concurrent use of anticholinergics and cholinesterase inhibitors: rare event or common practice?" J Am Geriatr Soc 52 (2004): 2082-7[4] Fick DM, Cooper JW, Wade WE, Waller JL, Maclean JR, Beers MH "Updating the Beers criteria for potentially inappropriate medication use in older adults: results of a US consensus panel of experts." Arch Intern Med 163 (2003): 2716-2724[5] Beers MH, Ouslander JG, Rollingher I, Reuben DB, Brooks J, Beck JC "Explicit criteria for determining inappropriate medication use in nursing home residents." Arch Intern Med 151 (1991): 1825-32[6] Roe CM, Anderson MJ, Spivack B "Use of anticholinergic medications by older adults with dementia." J Am Geriatr Soc 50 (2002): 836-42', 1767363022);
