INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65475, 'Doxepin (topical)', 'Cimetidine', 'Moderate', 'Topical application of doxepin can produce therapeutic concentrations, and drug interactions are therefore possible. Cimetidine may increase serum doxepin concentrations by inhibiting doxepin metabolism. Adverse effects may increase.', NULL, 'Patients should be advised to notify their physician if they experience increased sedation, blurry vision, severe dry mouth, or urinary retention.', NULL, 'Patients should be advised to notify their physician if they experience increased sedation, blurry vision, severe dry mouth, or urinary retention. Ranitidine has not been found to affect serum concentrations of tricyclic antidepressants and may be a better alternative.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65562/', '[1] "Product Information. Zonalon (doxepin topical)." Bioglan Pharmaceutical Inc, Malvern, PA.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65476, 'Dronedarone', 'Cimetidine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of dronedarone, which is primarily metabolized by the isoenzyme. Data are not available for dronedarone in combination with other, less potent CYP450 3A4 inhibitors.', NULL, 'Caution is advised if dronedarone is prescribed in combination with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if dronedarone is prescribed in combination with CYP450 3A4 inhibitors. Pharmacologic response to dronedarone should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. Patients should be advised to seek medical attention if they experience symptoms that could indicate dronedarone toxicity such as excessive fatigue, fainting, difficulty breathing, chest pain or tightness, bradycardia, or symptoms of torsade de pointes such as dizziness, palpitations, and syncope. Dronedarone should be discontinued if QTc Bazett interval increases to 500 ms or greater.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65563/', '[1] "Product Information. Multaq (dronedarone)." sanofi-aventis , Bridgewater, NJ.[2] "Product Information. Multaq (dronedarone)." sanofi-aventis , Bridgewater, NJ.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65477, 'Duloxetine', 'Cimetidine', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 may increase the plasma concentrations of duloxetine, which is a substrate of the isoenzyme. Theoretically, high plasma levels of duloxetine may increase the risk of serious adverse effects such as hypertension, hypertensive crisis, increased heart rate, orthostatic hypotension, syncope, and serotonin syndrome. Serotonin syndrome is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Symptoms may include mental status changes such as irritability, altered consciousness, confusion, hallucinations, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea.', NULL, 'Caution is advised if duloxetine is used in combination with CYP450 1A2 inhibitors.', NULL, 'Caution is advised if duloxetine is used in combination with CYP450 1A2 inhibitors. Pharmacologic response to duloxetine should be monitored more closely whenever a CYP450 1A2 inhibitor is added to or withdrawn from therapy, and the dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65564/', '[1] Brosen K, Skjelbo E, Rasmussen BB, Poulsen HE, Loft S "Fluvoxamine is a potent inhibitor of cytochrome P4501A2." Biochem Pharmacol 45 (1993): 1211-4[2] Martinez C, Albet C, Agundez JA, et al. "Comparative in vitro and in vivo inhibition of cytochrome P450 CYP1A2, CYP2D6, and CYP3A by H2-receptor antagonists." Clin Pharmacol Ther 65 (1999): 369-76[3] "Product Information. Cymbalta (duloxetine)." Lilly, Eli and Company, Indianapolis, IN.[4] Uwe F, Strobl G, Manaut F, et al "Quinolone antibacterial agents: relationship between structure and in vitro inhibition of the human cytochrome P450 isofaorm CYP1A2." Mol Pharmacol 43 (1993): 191-9[5] "Product Information. Cymbalta (duloxetine)." Lilly, Eli and Company, Indianapolis, IN.', 1767363022);
