INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65490, 'Erlotinib', 'Cimetidine', 'Moderate', 'INTERVAL: Concurrent administration of agents that increase gastric pH such as H2-receptor antagonists may decrease the oral bioavailability of erlotinib and reduce its concentrations in plasma. The solubility of erlotinib decreases when the pH is over 5, resulting in reduced absorption.', NULL, 'In general, the concomitant use of erlotinib with agents that increase gastric pH should be avoided whenever possible.', NULL, 'In general, the concomitant use of erlotinib with agents that increase gastric pH should be avoided whenever possible. If acid-suppression therapy is required during erlotinib treatment, an H2-receptor antagonist such as ranitidine may be considered. Available pharmacokinetic data suggest that erlotinib should be administered once a day, at least 2 hours before or 10 hours after dosing of the H2-receptor antagonist. Additionally, the lowest effective dosage of the H2-receptor antagonist should be prescribed. It may be appropriate to avoid using cimetidine for long-term acid suppression, as it may inhibit the CYP450 3A4-mediated metabolism of erlotinib and increase the risk of toxicity.', 'Metabolism, Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65577/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Duong S, Leung M "Should the concomitant use of erlotinib and acid-reducing agents be avoided? The drug interaction between erlotinib and acid-reducing agents." J Oncol Pharm Pract 17 (2011): 448-52[4] "Product Information. Tarceva (erlotinib)." Genentech, South San Francisco, CA.[5] Li J, Zhao M, He P, Hidalgo M, Baker SD "Differential metabolism of gefitinib and erlotinib by human cytochrome p450 enzymes." Clin Cancer Res 13 (2007): 3731-7[6] "Product Information. Tarceva (erlotinib)." Genentech, South San Francisco, CA.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65491, 'Erythromycin', 'Cimetidine', 'Moderate', 'Concomitant use of cimetidine and erythromycin may result in higher-than-normal plasma levels of erythromycin through inhibition of CYP450 metabolism by cimetidine.', NULL, 'The patient should be monitored for evidence of adverse effects such as nausea, vomiting, diarrhea, or abdominal pain.', NULL, 'The patient should be monitored for evidence of adverse effects such as nausea, vomiting, diarrhea, or abdominal pain. Ranitidine, nizatidine and famotidine do not inhibit CYP450 and would not be expected to affect erythromycin levels at recommended doses.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65578/', '[1] Mogford N, Pallett A, George C "Erythromycin deafness and cimetidine treatment." BMJ 309 (1994): 1620[2] "Product Information. Axid (nizatidine)." Lilly, Eli and Company, Indianapolis, IN.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] "Product Information. Zantac (ranitidine)." Glaxo Wellcome, Research Triangle Park, NC.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65492, 'Escitalopram', 'Cimetidine', 'Moderate', 'Coadministration with cimetidine may increase the plasma concentrations of escitalopram. The proposed mechanism is cimetidine inhibition of the metabolic clearance of escitalopram via CYP450 2C19, 2D6, and 3A4.High plasma levels of escitalopram may increase the risk of serious side effects such as QT prolongation, which may lead to arrhythmias including torsade de pointes and sudden death, as well as serotonin syndrome, which is a rare but potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Symptoms of the serotonin syndrome may include mental status changes such as irritability, altered consciousness, confusion, hallucination, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea.', NULL, 'Caution is advised if escitalopram must be used in combination with cimetidine.', NULL, 'Caution is advised if escitalopram must be used in combination with cimetidine. Pharmacologic response to escitalopram should be monitored more closely whenever cimetidine is added to or withdrawn from therapy, and the escitalopram dosage adjusted as necessary. This may be particularly important in patients receiving escitalopram at the upper end of the dosage range. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheaded