INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65502, 'Everolimus', 'Cimetidine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein may increase the blood concentrations of everolimus following oral administration. Everolimus is a substrate of both the CYP450 3A4 isoenzyme and P-glycoprotein drug efflux transporter, thus their inhibition in the intestine can enhance the absorption of everolimus.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of everolimus therapy should be considered during coadministration with CYP450 3A4 and/or P-glycoprotein inhibitors, including adverse effects such as pneumonitis, stomatitis, infection, dyspnea, diarrhea, anemia, leucopenia, thrombocytopenia, hyperglycemia, and hyperlipidemia.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of everolimus therapy should be considered during coadministration with CYP450 3A4 and/or P-glycoprotein inhibitors, including adverse effects such as pneumonitis, stomatitis, infection, dyspnea, diarrhea, anemia, leucopenia, thrombocytopenia, hyperglycemia, and hyperlipidemia. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65589/', '[1] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.[4] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767363022);
