INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65509, 'Fesoterodine', 'Cimetidine', 'Moderate', 'Coadministration with inhibitors of CYP450 2D6 may increase the plasma concentrations of fesoterodine''s active metabolite, 5-hydroxymethyl tolterodine, which is partially metabolized by the isoenzyme.', NULL, 'No initial dosing adjustments are recommended when fesoterodine is used in combination with CYP450 2D6 inhibitors.', NULL, 'No initial dosing adjustments are recommended when fesoterodine is used in combination with CYP450 2D6 inhibitors. Patients should be advised to notify their physician if they experience potential adverse effects of fesoterodine such as irregular heartbeat, blurry vision, difficulty urinating, dry mouth, headache, drowsiness, dizziness, gastrointestinal upset, or constipation.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65596/', '[1] "Product Information. Toviaz (fesoterodine)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] "Product Information. Toviaz (fesoterodine)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65510, 'Fingolimod', 'Cimetidine', 'Moderate', 'The risk of bradycardia and atrioventricular (AV) block may be increased during initiation of fingolimod treatment in patients receiving beta-blockers, calcium channel blockers, digitalis, or other drugs that can slow the heart rate or AV conduction such as alectinib, atazanavir, flecainide, ivabradine, lacosamide, lithium, mefloquine, moricizine, propafenone, succinylcholine, thalidomide, H2-receptor antagonists, tricyclic antidepressants, and anticholinesterase or cholinergic agents.', NULL, 'Fingolimod has not been adequately studied in patients receiving other drugs that can slow the heart rate or AV conduction.', NULL, 'Fingolimod has not been adequately studied in patients receiving other drugs that can slow the heart rate or AV conduction. Close monitoring is recommended during initiation of fingolimod treatment in these patients. The first dose should always be administered in a setting where resources to appropriately manage symptomatic bradycardia are available.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65597/', '[1] FDA. U.S. Food and Drug Administration "FDA Drug Safety Communication: Revised recommendations for cardiovascular monitoring and use of multiple sclerosis drug Gilenya (fingolimod). Available from: URL: http://www.fda.gov/Drugs/DrugSafety/ucm303192.htm#data." ([2012 May 14]):[2] "Product Information. Gilenya (fingolimod)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65511, 'Flecainide', 'Cimetidine', 'Moderate', 'Cimetidine may decrease the hepatic biotransformation or increase the bioavailability of flecainide. The time during which maximum plasma flecainide concentrations occur may be prolonged. No special precautions appear to be necessary in patients with normal renal function.', NULL, 'Clinical monitoring for altered response and dosage reductions may be required in patients with renal impairment.', NULL, 'Clinical monitoring for altered response and dosage reductions may be required in patients with renal impairment. Famotidine, nizatidine and ranitidine are not expected to interact and may be considered as alternatives.', 'Metabolism, Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65598/', '[1] Tjandra-Maga TB, Van Hecken A, Van Melle P, Verbesselt R, de Schepper PJ "Altered pharmacokinetics of oral flecainide by cimetidine." Br J Clin Pharmacol 22 (1986): 108-10', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65512, 'Flibanserin', 'Cimetidine', 'Moderate', 'Coadministration with weak inhibitors of CYP450 3A4 may increase the plasma concentrations of flibanserin, which is primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C19.', NULL, 'Caution is advised when flibanserin is used with multiple weak CYP450 3A4 inhibitors.', NULL, 'Caution is advised when flibanserin is used with multiple weak CYP450 3A4 inhibitors. Patients should be monitored for signs and symptoms of hypotension, syncope, and central nervous system depression.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65599/', '[1] "Product Information. Addyi (flibanserin)." Sprout Pharmaceuticals, Raleigh, NC.[2] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.[3] "Product Information. Addyi (flibanserin)." Sprout Pharmaceuticals, Raleigh, NC.', 1767363022);
