INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65525, 'Hydroxychloroquine', 'Cimetidine', 'Moderate', 'Coadministration with cimetidine may increase the plasma concentrations of chloroquine. The proposed mechanism is cimetidine inhibition of chloroquine metabolism via hepatic microsomal enzymes.', NULL, 'Concomitant use of chloroquine and cimetidine should be avoided.', NULL, 'Concomitant use of chloroquine and cimetidine should be avoided. Other H2-receptor antagonists such as famotidine, nizatidine, and ranitidine may be considered during chloroquine treatment, since they are not known to interfere with cytochrome P450 isoenzymes to any significant extent. The same precaution may be applicable to hydroxychloroquine, although no data are available to support this recommendation.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65612/', '[1] Ette EI, Brown-Awala EA, Essien EE "Chloroquine elimination in humans: effect of low-dose cimetidine." J Clin Pharmacol 27 (1987): 813-6[2] "Product Information. Aralen (chloroquine)." Sanofi Winthrop Pharmaceuticals, New York, NY.[3] Haagsma CJ "Clinically important drug interactions with disease-modifying antirheumatic drugs." Drugs Aging 13 (1998): 281-9[4] Ette EI, Brown-Awala EA, Essien EE "Effect of ranitidine on chloroquine disposition." Drug Intell Clin Pharm 21 (1987): 732-4[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65526, 'Hydroxyzine', 'Cimetidine', 'Minor', 'Cimetidine has been shown to increase the serum concentration of hydroxyzine. The clinical significance of this interaction is not known. The mechanism may be cimetidine-induced inhibition of hydroxyzine metabolism. If cimetidine and hydroxyzine are coadministered, the patient should be monitored for excessive somnolence.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65613/', '[1] Simons FE, Sussman GL, Simons KJ "Effect of the H2-antagonist cimetidine on the pharmacokinetics and pharmacodynamics of the H1-antagonists hydroxyzine and cetirizine in patients with chronic urticaria." J Allergy Clin Immunol 95 (1995): 685-93[2] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[3] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[4] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65527, 'Ibrutinib', 'Cimetidine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ibrutinib, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if ibrutinib is prescribed in combination with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if ibrutinib is prescribed in combination with CYP450 3A4 inhibitors. Patients should be closely monitored for signs of ibrutinib toxicity such as myelosuppression, bleeding, infection, and renal impairment. Dosage reduction in accordance with ibrutinib product labeling may be necessary if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65614/', '[1] "Product Information. Imbruvica (ibrutinib)." Pharmacyclics Inc, Sunnyvale, CA.[2] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Imbruvica (ibrutinib)." Pharmacyclics Inc, Sunnyvale, CA.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65528, 'Imatinib', 'Cimetidine', 'Moderate', 'The coadministration with drugs that are inhibitors of the CYP450 3A4 enzymatic pathway may increase the plasma concentrations of imatinib, which is metabolized by CYP450 3A4.', NULL, 'While clinical data are lacking, the possibility of prolonged and/or increased pharmacologic effects of imatinib, including serious adverse effects such as edema, hematologic toxicity and immunosuppression, should be considered during concomitant therapy.', NULL, 'While clinical data are lacking, the possibility of prolonged and/or increased pharmacologic effects of imatinib, including serious adverse effects such as edema, hematologic toxicity and immunosuppression, should be considered during concomitant therapy. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65615/', '[1] "Product Information. Gleevec (imatinib mesylate)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Gleevec (imatinib mesylate)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65529, 'Imipramine', 'Cimetidine', 'Moderate', 'Cimetidine may increase the serum concentration of tricyclic antidepressants (TCAs) to toxic levels in some patients. The mechanism may be related to inhibition of CYP450 isoenzymes, including CYP450 2D6 and CYP450 3A4, that are responsible for the hepatic metabolism of TCAs.', NULL, 'Close monitoring for clinical response and tolerance is recommended whenever cimetidine is added to or discontinued from a TCA regimen.', NULL, 'Close monitoring for clinical response and tolerance is recommended whenever cimetidine is added to or discontinued from a TCA regimen. Patients should be advised to notify their physician if they experience excessive TCA effects such as dry mouth, blurry vision, irregular or fast heartbeat, constipation, urinary retention, dizziness, or orthostatic hypotension. Lower doses of tricyclic antidepressants may be necessary. Ranitidine, famotidine, and nizatidine are not expected to interact and may be an alternative to cimetidine in this circumstance.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65616/', '[1] Abernethy DR, Greenblatt DJ, Shader RI "Imipramine-cimetidine interaction: impairment of clearance and enhanced absolute bioavailability." J Pharmacol Exp Ther 229 (1984): 702-5[2] Sutherland DL, Remillard AJ, Haight KR, Brown MA, Old L "The influence of cimetidine versus ranitidine on doxepin pharmacokinetics." Eur J Clin Pharmacol 32 (1987): 159-64[3] Henauer SA, Hollister LE "Cimetidine interaction with imipramine and nortriptyline." Clin Pharmacol Ther 35 (1984): 183-7[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Curry SH, DeVane CL, Wolfe MM "Lack of interaction of ranitidine with amitriptyline." Eur J Clin Pharmacol 32 (1987): 317-20[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] Wells BG, Pieper JA, Self TH, et al "The effect of ranitidine and cimetidine on imipramine disposition." Eur J Clin Pharmacol 31 (1986): 285-90[8] Miller DD, Macklin M "Cimetidine-imipramine interaction: a case report." Am J Psychiatry 140 (1983): 351-2', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65530, 'Indomethacin', 'Cimetidine', 'Minor', 'H2 antagonists may alter the disposition of nonsteroidal anti-inflammatory drugs (NSAIDs), resulting in increased or decreased plasma concentrations. Data are varied, even for the same NSAID. The mechanism may be related to inhibition of metabolism, changes in gastric pH that decrease absorption, and/or reduced urinary elimination. Statistically significant changes have been small and of limited clinical significance. Clinical monitoring of patient response and tolerance is recommended.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65617/', '[1] Said SA, Foda AM "Influence of cimetidine on the pharmacokinetics of piroxicam in rat and man." Arzneimittelforschung 39 (1989): 790-2[2] "Product Information. Daypro (oxaprozin)." Searle, Skokie, IL.[3] Scavone JM, Greenblatt DJ, Matlis R, Harmatz JS "Interaction of oxaprozin with acetaminophen, cimetidine, and ranitidine." Eur J Clin Pharmacol 31 (1986): 371-4', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65531, 'Irinotecan', 'Cimetidine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or UGT1A1 may increase the plasma concentrations of irinotecan and its active metabolite, SN-38. CYP450 3A4 and UGT1A1 are the isoenzymes responsible for the metabolic conversion of irinotecan to its inactive metabolite, APC. Inhibition of APC formation results in more irinotecan metabolism to SN-38, an active and toxic metabolite. High plasma levels of irinotecan and SN-38 may increase the risk of potentially fatal toxicities such as severe diarrhea, neutropenia, sepsis, and thromboembolism.', NULL, 'Caution is advised when irinotecan is prescribed with CYP450 3A4 or UGT1A1 inhibitors.', NULL, 'Caution is advised when irinotecan is prescribed with CYP450 3A4 or UGT1A1 inhibitors. Patients should be monitored for toxicities such as diarrhea, myelosuppression, thromboembolism, and interstitial lung disease, and the irinotecan dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65618/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Corona G, Vaccher E, Sandron S, et al. "Lopinavir-ritonavir dramatically affects the pharmacokinetics of irinotecan in HIV patients with Kaposi''s sarcoma." Clin Pharmacol Ther 83 (2008): 601-6[4] "Product Information. Onivyde (irinotecan liposomal)." Merrimack Pharmaceuticals, Cambridge, MA.[5] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[6] Phansalker S, Desai AA, Bell D, et al "High-priority drug-drug interactions for use in electronic health records." J Am Med Inform Assoc 19 (2012): 735-43[7] "Product Information. Camptosar (irinotecan)." Pharmacia and Upjohn, Kalamazoo, MI.[8] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.', 1767363022);
